css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#downloadManager #downloadView {
	padding: 1px 0 !important;
	background: #FCFCFC !important}

richlistitem[type="download"] {
	-moz-appearance: none !important;
	outline: none !important;
	background: none !important;
	background-clip: padding-box !important;
	border-radius: 1px !important;
	border: 1px solid Transparent !important;
	border-bottom-color: hsla(220,18%,51%,.25) !important;
	padding: 3px 5px !important;
	margin: 0 2px -1px !important;
	color: #434E54 !important}

richlistitem[type="download"]:last-child {
	border-bottom-color: Transparent !important}

richlistitem[type="download"] .name,
richlistitem[type="download"] .dateTime {
	color: Black !important}

richlistitem[type="download"]:not([selected="true"]):hover,
richlistitem[type="download"][state="4"]:not([selected="true"]):hover,
richlistitem[type="download"][state="2"]:not([selected="true"]):hover,
richlistitem[type="download"][state="3"]:not([selected="true"]):hover {
	border-radius: 2.5px !important;
	border-color: hsla(213,53%,67%,.44) !important;
	background: -moz-linear-gradient(hsl(220,43%,99%), hsl(213,82%,96%)) !important;
	box-shadow: 0 0 0 1px hsla(0,0%,100%,.5) inset,
				0 1px 0 hsla(0,0%,100%,.3) inset !important;
	-moz-transition: .2s ease-in !important}

#downloadManager:-moz-window-inactive richlistitem[type="download"]:not([state="3"]):not([state="2"])[selected="true"],
#downloadManager:-moz-window-inactive richlistitem[type="download"][state="0"][selected="true"] {
	border-radius: 2.5px !important;
	border-color: hsl(0,1%,85%) !important;
	background: -moz-linear-gradient(hsl(180,8%,97%), hsl(0,0%,92%)) !important;
	box-shadow: 0 0 0 1px hsla(0,0%,100%,.5) inset,
				0 1px 0 hsla(0,0%,100%,.3) inset !important}

richlistitem[type="download"][selected="true"],
richlistitem[type="download"][selected="true"]:hover,
#downloadManager richlistitem[type="download"]:not([state="3"]):not([state="2"])[selected="true"]:hover {
	border-radius: 2.5px !important;
	border-color: hsl(213,45%,65%) !important;
	background: -moz-linear-gradient(hsl(212,86%,92%), hsl(212,91%,86%)) !important;
	box-shadow: 0 0 0 1px hsla(0,0%,100%,.5) inset,
				0 1px 0 hsla(0,0%,100%,.3) inset !important}

richlistitem[type="download"][state="2"] .name,
richlistitem[type="download"][state="2"] .dateTime,
richlistitem[type="download"][state="3"] .name,
richlistitem[type="download"][state="3"] .dateTime {
	color: InactiveCaptionText !important}

richlistitem[type="download"][state="3"] .downloadTypeIcon,
richlistitem[type="download"][state="4"] .downloadTypeIcon {
	filter: url("chrome://mozapps/skin/extensions/extensions.svg #greyscale") !important}

#downloadManager #clearListButton{
	margin: 2px !important}

#downloadManager textbox {
	-moz-appearance: none !important;
	margin: 2px !important;
	padding: 2px !important;
	background-color: rgba(255,255,255,.725) !important;
	background-clip: padding-box !important;
	border: 1px solid rgba(0,0,0,.32) !important;
	border-radius: 2.5px !important}

#downloadManager textbox:hover {
	background-color: rgba(255,255,255,.898) !important}

#downloadManager textbox[focused="true"] {
	background-color: White !important}

#downloadManager .mini-button {
	list-style-image: url("data:image/png;base64,") !important}
