css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(userstyles.org) {

footer a[href='/contact'], nav a[href='/help'], #donate, #install h2, section h2, figcaption {
  display: none !important;
  }
header {
  background: #e6650f !important;
  border-radius: 0px !important;
  margin-left: 0px !important;
  padding-top: 12px !important;
  padding-bottom: 12px !important;
  }
header, header a:link, header a:visited {
  color: #fff !important;
  text-decoration: none !important;
  }
#install {
  background: #fff !important;
  border-radius: 0px !important;
  }
#screenshots {
  border-radius: 0px !important;
  background: none !important;
  }
#left-sidebar, #left-sidebar nav, #left-sidebar footer {
  background: #3f999f !important;
  }
#left-sidebar a:link, #left-sidebar h2, #left-sidebar nav a:visited {
  color: #eee !important;
  text-decoration: none !important;
  }
#left-sidebar #related a:visited {
  color: #bbb !important;
  }
#left-sidebar #related .more a:visited {
  color: #fff !important;
  }
#left-sidebar li, .more, #style-author, #long-description {
  font-size: 14px !important;
  }
#related h2 {
  font-size: 16px !important;
  padding-bottom: 5px !important;
  }
#long-description, #style-author {
  margin-left: 20px !important;
  }
article a:link {
  color: #206cb4 !important;
  text-decoration: none !important;
  }
article a:visited {
  color: #666 !important;
  }
#show-code {
  padding: 10px 0px 10px 0px !important;
  }
#install {
  height: 44px !important;
  }
article div[id*='front-page'] h2 {
  margin-left: 36px !important;
  }
article div[id*='front-page'] ul, #style-list {
  visibility: hidden !important;
  }
article ul a, #discussions-area ul, #style-list li a {
  visibility: visible !important;
  }
.pagination {
  margin-left: 40px !important;
  font-size: 16px !important;
  }
}

