css:
@-moz-document domain("facebook.com") {

a[href*="/ajax/groups/create_get.php"] {
 display: none !important; 
}

#globalContainer #pageHead, .loggedout_menubar  {
  width: 940px !important;
  left: 93px;
  z-index: 15 !important;
}
#pageHead {
  position: fixed !important;
  width: 981px !important;
  margin: 0 auto !important;
  top: 0px;
  z-index: 15 !important;
}
#blueBar {
  position: fixed !important;
  top: 0px;
  width: 100%;
  visibility: visible;
  -webkit-box-shadow: 0px 2px 8px rgba(0,0,0,0.3);
  -moz-box-shadow: 0px 2px 8px rgba(0,0,0,0.3);
  z-index: 14 !important;
}
#content {
  padding-top: 35px !important;
}
body.UIPage_LoggedOut #content {
  padding-top: 0 !important;
}
.loggedout_menubar {
  position: fixed;
}

body.ego_page #mainContainer > #leftCol {
  position: fixed;
  top: 35px;
}

li#navAccount:not(.openToggler) > ul > li:last-child {
  position: absolute;
  left: 50%;
  width: 59px;
  margin-left: -147px;
  top: -30px;
  visibility: visible !important;
}
li#navAccount:not(.openToggler) > ul > li:last-child input {
  border: 0 !important;
  color: #FFF !important;
  font-weight: bold !important;
  height: 30px !important;
  padding-top: 4px !important;
}
li#navAccount:not(.openToggler) > ul > li:last-child input:hover{
  background-color: #6D86B7 !important;
  padding-bottom: 5px !important;
}
li#navAccount ul {
  display: block !important;
  visibility: hidden;
}
#navAccount.openToggler ul {
  visibility: visible;
}

.jewel { margin-right: 1px !important; }
.jewelNew {
  background: url('data:image/gif;base64,R0lGODlhAQABAIAAAP///zpalCH/C05FVFNDQVBFMi4wAwEAAAAh+QQJeAABACwAAAAAAQABAAACAkQBACH5BAl4AAEALAAAAAABAAEAAAICTAEAOw==') !important;
  -moz-border-radius: 6px;
  /*border: solid 1px white !important;*/
  display: block;
  height: 29px !important;
}

#headNav, .jewel, .jewelToggler, #pageLogo > a {
  -webkit-border-top-left-radius: 4px;
  -webkit-border-top-right-radius: 4px;
  -moz-border-radius-topleft: 4px;
  -moz-border-radius-topright: 4px;
}
#pageNav #navAccount > a, li.openToggler {
  -webkit-border-top-left-radius: 5px;
  -webkit-border-top-right-radius: 5px;
  -moz-border-radius-topleft: 5px;
  -moz-border-radius-topright: 5px;
}

#navAccount > ul, .jewelBox, .uiOverlay, #navSearch .uiTypeaheadView,  .pop_container_advanced {
  -webkit-box-shadow: 3px 3px 20px rgba(0,0,0,0.4);
  -moz-box-shadow: 3px 3px 20px rgba(0,0,0,0.4);
}
#fbDockChat, .fbNubFlyout, .uiMenu {
  -webkit-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
  -moz-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
}
form .uiTypeaheadView {
  z-index: -10 !important;
  border-top: 0;
}

#ConfirmBannerOuterContainer {
  position: relative !important;
  top: 40px;
}

@-webkit-keyframes slidefromtop {
from {
  opacity: 0;
  -webkit-transform: translateY(-10px);
}}
.jewelBox, .uiSelectorMenuWrapper, .uiTypeaheadView {
  -webkit-animation: slidefromtop 0.3s;
}

@-webkit-keyframes slidefrombottom {
from {
  opacity: 0;
  -webkit-transform: translateY(10px);
}}
.fbNubFlyout, .hovercard, .uiTooltipWrap {
  -webkit-animation: slidefrombottom 0.3s;
}

@-webkit-keyframes slidefromtoplarge {
from {
  opacity: 0;
  -webkit-transform: translateY(-200px);
}}
.pop_dialog {
  -webkit-animation: slidefromtoplarge .6s;
}

label.uiLinkButton {
display: none !important;
}
label.logoutButton {
display: none !important;
}

div#pagelet_adbox {
display: none !important;
}
div#footerContainer {
display: none !important;
}

#pagelet_composer {
position: fixed !important;
display: block !important;
background-color: #ffffff !important;
left: 0px !important;
bottom: 0px !important;
padding: 20px !important;
-moz-border-radius: 10px !important;
z-index: 1000 !important;
}

div.ego_section {
display: none !important;
}

input {
text-align: center !important;
}

a[accesskey*="2"] {
 display: none !important; 
}

div[id*="leftCol"] {
 position: fixed !important;
}

}
