css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("github.com") {

#files .file .meta .info, 
.file-box .meta .info,
#files .file .data pre,
#files .file .line-data, 
#files .file .line-number, 
.file-box .data pre, 
.file-box .line-data, 
.file-box .line-number,
pre, code, .tree-browser,
.file-editor-textarea,
textarea.commit-message {
  font-family: Monaco,Consolas,"DejaVu Sans Mono","Bitstream Vera Sans Mono",monospace !important;
}

.topsearch form input.search {
  font: 12px helvetica,arial,freesans,clean,sans-serif;
}

div.site {
  margin: auto !important;
  width: 974px !important;
}

.pagehead, .tree-browser,
#slider .frames,
#slider .frames .frame
{ width: 100% !important; }

.pagehead {
  background: #ffffff !important;
  box-shadow: 0 0 2px #CCCCCC;
  border-radius: 5px 5px 0 0 !important;
  padding: 5px 10px !important;
  margin: -20px 0 15px -10px !important;
}

.breadcrumb {
background: none repeat scroll 0 0 #FAFAFA;
  border-right: 1px solid #DDDDDD;
  border-left: 1px solid #DDDDDD;
  padding: 10px !important;
}

#clippy { display: none !important; }

#slider .frames .big-actions { right: 12px !important; top: 12px !important; margin: 0 !important; }

ul.big-actions { margin: 12px !important; }

.file-editor-textarea,
textarea.commit-message {
  width: 963px !important;
}

#files .file .meta, .file-box .meta {
  width: 962px !important;
  overflow: visible !important;
}

/* Theme */

/* comment */
.highlight .c,
.highlight .c1,
.highlight .cm,
.highlight .sd { 
  color: #036A07!important;
  font-style: italic !important;
}

}
