@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnn.com") {

/* turn off display of */
#cnn_toptstmparea,  
#hdr-editions,      
#hdr-auth,          
.cnnOpin,           
#cnn_hdr-prompt,
#nav-newspulse img, 
#nav-money img,     
#nav-sports img,
#js-dish     
{ display: none !important; }


.xxcnn_adunit970x66,   
.xxcnn_adtitle,        
.xxcnn_adcntr300x100,  
.xxcnn_adbygbin,        
.xxcnn_adcntr728x90t,
#xxad-63996,
#xxad-14582,
#xcnn_awsmbr_1,        
#ew1552816_bannerDiv,  
#_fw_container_medium_rectangle, 
#qa_wallpaper a,     
div[class^="cnn_career"],
div[class^="cnn_ad"],
div[id^="ad-"],
div[id^="cnn_aw"],
form[id^="cnn_cb"],
div[id^="Vw"],
div[id*="HoverAd"]
{ display: none !important; }

/* turn facebook, twitter, sharing tools */
.c_sharelftcol { 
    display: none !important;
}


/* turn off ad in "from our shows" 5th block is always an ad*/
.cnn_fabcatz:nth-of-type(5) 
{ display:none !important; }

#hdr-search form {
  background-image: none !important;
}

/* fix for iReport page that uses #cnn-hdr instead of standard #cnn_hdr*/
#cnn-hdr{
    height: 50px !important;
}
#cnn-hdr-nav {
    padding-top: 18px !important;
    background: none !important;
}
#hdr-banner-skinny a:before{
  content: "cnn" !important;;
  color: #FFFFFF!important;
  font: bold 28px Tahoma, Geneva, sans-serif;
  padding-left: 3px;
  position:relative !important;
  top: -26px !important;
}
#cnn-hdr {
  border-bottom: 3px solid #DFDFDF !important;
}
#cnn-hdr .nav-menu-skinny li a {
  color: White !important;
  text-decoration: none !important;
  border-right: 1px solid #C2C8CF !important;
}
#cnn-hdr-nav ul li a:hover {
  background-color: #0b4682 !important; 
}
#ir-login-home h3 {
  background-color: #003365 !important;
}
.ir_login_subnav a {
  color: #003365 !important;
}
img[src*="cnniReport.jpg"] {
  display: none !important;
}
#ir_sitelogo a:before{
  content: "iReport" !important;;
  color: #003365 !important;
  font: bold 38px Tahoma, Geneva, sans-serif;
  padding-left: 3px;
  position:relative !important;
  top: 5px !important;
}
#ftr-bottom {
  border-top: 11px solid #003365 !important;
}
/* end fix */


#cnn_hdr-main img {
  height: 30px !important;
  width: 55px !important;  
}


#cnn_hdr h1, #cnn_hdr-main { 
  text-align: left !important; 
}


#cnn_hdr-main { 
  height: 30px !important; 
  border-bottom: none !important;
}


#hdr-search
{ top: 5px !important;  }



#cnn_hdr-nav, 
#us-menu, 
#us-menu li, #intl-menu li, 
#us-menu li a, #intl-menu li a {
  height: 20px !important;
  line-height: 25px !important;
  background: none !important;
}


#nav-home {  width: 50px !important; }
#nav-video {  width: 50px !important; }
#nav-newspulse {  width: 75px !important; }

#cnn_hdr-nav ul {
  background-color: #003365 !important;
  background-image: none !important;  
  color: White !important;
  font: normal 11px arial, helvetica, sans-serif !important;
}
#cnn_hdr-nav ul li a {
  color: White !important;
  text-decoration: none !important;
  background-image: none !important;
}


#cnn_hdr-nav {
  border-bottom: 3px solid #DFDFDF !important;
}


.cnn_ftrdivl2 {
  background-color: #003365 !important;
}


#cnn_hdr-main, #cnn_hdr-nav, #cnn_hdr-nav ul  {
 background-color: #003365 !important
}
#cnn_hdr #us-menu, #cnn_hdr #intl-menu {
 border-left: 1px solid #003365 !important;
}
#us-menu li, #intl-menu li {
 border-right: 1px solid #003365 !important;
}
#cnn_hdr-nav ul li a {
  color: White !important;
  text-decoration: none !important;
  border-right: 1px solid #C2C8CF !important;
}
#cnn_hdr-nav ul li a:hover {
  background-color: #0b4682 !important;  
}



img[src*="hdr-main.gif"],
img[src*="hdr-us.gif"],
img[src*="hdr-world.gif"],
img[src*="hdr-justice.gif"],
img[src*="hdr-entertainment.gif"],
img[src*="hdr-tech.gif"],
img[src*="hdr-health.gif"],
img[src*="hdr-living.gif"],
img[src*="hdr-travel.gif"],
img[src*="hdr-opinion.gif"],
img[src*="hdr-politics.gif"] {
  display: none !important;
}
/*#hdr-banner-title:before {
  content: "cnn" !important;;
  color: #FFFFFF!important;
  font: bold 28px Tahoma, Geneva, sans-serif;
  padding-left: 3px;
}*/

.cnn-hdr {
  background: none repeat scroll 0 0 #00153E !important;
}


.cnn_hdrsnvcntr, 
.cnn_hdrsnv,
#cnn_fabcprev,
#cnn_fabcnext{
  background-color: #003365 !important;  
}

img[src*="nav-arrow.gif"],
img[src*="CNN_logo.gif"],
img[src*="tzavtr.cnn.logo.gif"] {
  display: none !important;
}

.cnn_hdrsnvl:before{
  content: "cnn" !important;;
  color: #FFFFFF!important;
  font: bold 28px Tahoma, Geneva, sans-serif;
  padding-left: 3px;
  position:relative !important;
  top: -8px !important;
}


#cnn_hdr, .cnn_hdrsnvcntr, #cnn_hdr_vid, #cnn-hdr {
  background: #00153e url(data:image/jpeg;base64,) no-repeat 0 -10px !important;
}
#cnn_hdr-main, #cnn_hdr-nav, #cnn_hdr-nav ul  {
  background: none !important;
}
/* remove vertical line between navigation on video page */
#cnn_hdr_vid .vidSwitch {
  border-left:none !important;
}
}
