css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/*
 * Dark'n Blue Transparent Theme
 * By      : Pirlouit
 * Date    : February 23, 2011
 */

/*FINDBAR*/

#FindToolbar {
          color: black !important;
          background: -moz-linear-gradient(top, rgb(99,99,99) , rgb(77,77,77) ) padding-box !important;
          -moz-appearance: none!important;
          border: none !important;
}

.findbar-find-next, .findbar-find-previous, .findbar-highlight {
          color: lightgray !important;
}

.findbar-find-next:hover, .findbar-find-previous:hover, .findbar-highlight:hover {
          color:black !important;
}

/*MENU*/

popupnotification, .panel-inner-arrowcontent, #identity-popup-container, #identity-popup,.popup-internal-box, #contentAreaContextMenu {
          -moz-appearance: none !important;
          background: -moz-linear-gradient(top, rgb(99,99,99) , rgb(77,77,77) ) padding-box !important;
}

tooltip {
          -moz-appearance: none !important;
          border: 1px solid black !important;
          color: black !important;
          font-size: 12px !important;
          background: -moz-linear-gradient(top, rgb(99,99,99) , rgb(77,77,77) ) padding-box !important;
}

menuseparator {
          -moz-appearance: none !important;
          background: none !important;
          margin-top: 3px!important;
          margin-bottom: -5px!important;
          margin-left: 30px!important;
          border-top: solid 1px black !important;
          border-bottom: none !important;
}

popup, menupopup {
          -moz-border-radius: 3px !important;
          -moz-appearance: none !important;
          background: -moz-linear-gradient(top, rgb(99,99,99) , rgb(77,77,77) ) padding-box !important;
          border: 2px solid black !important;
          padding : 3px!important;
}

popup:not(#autoscroller),
popup menu,
popup menuitem,
menupopup menu,
menupopup menuitem {
          -moz-appearance: none !important;
}

popup:not([type="autocomplete"]) menu,
popup:not([type="autocomplete"]) menuitem,
*:not(menulist) > menupopup menu,
*:not(menulist) > menupopup menuitem {
          -moz-appearance: none !important;
          min-height: 22px !important;
          border: 1px solid !important;
          -moz-border-radius: 3px;
          -moz-border-top-colors: transparent !important;
          -moz-border-bottom-colors: transparent !important;
          -moz-border-left-colors: transparent !important;
          -moz-border-right-colors: transparent !important;
}

popup:not([type="autocomplete"]) menu[_moz-menuactive="true"],
popup:not([type="autocomplete"]) menuitem[_moz-menuactive="true"],
*:not(menulist) > menupopup menu[_moz-menuactive="true"],
*:not(menulist) > menupopup menuitem[_moz-menuactive="true"] {
          background: -moz-linear-gradient(top, rgb(89,89,89) , rgb(67,67,67) ) padding-box !important;
          -moz-border-top-colors: black !important;
          -moz-border-right-colors: black !important;
          -moz-border-bottom-colors: black !important;
          -moz-border-left-colors: black !important;
          -moz-box-shadow: inset #33CCFF 0px 0px 8px 4px!important;
}

/*APP MENU BUTTON*/

#appmenu-button {
          padding-left: 10px !important;
          padding-right: 10px !important;
          margin-left: 5px !important;
          background: -moz-linear-gradient(top, rgb(99,99,99) , rgb(77,77,77) )!important;
          border: none !important;
          box-shadow: inset 0 5px 6px rgb(220,220,220) !important;
}

#appmenu-button .button-box .box-inherit:before {
          content: "Menu"!important;
}

#appmenu-button:hover {
          font-style:italic !important;
          background: -moz-linear-gradient(top, #333333, #33CCFF)!important;
          box-shadow: 0px 0px 3px 3px #33CCFF !important;
}

#appmenu-button .button-box .box-inherit .button-text {
          display: none!important;
}

/*SIDEBAR HEADER*/

#bookmarks-view, #historyTree, #UpdateScan-window, .sidebar-placesTree {
          -moz-appearance: none !important;
          border: none !important;
          color: lightgray !important;
          background-color: #333333 !important;
          -moz-box-shadow: inset 0 0 1em black !important;
}

sidebarheader {
          -moz-appearance: none !important;
          background-color: #333333 !important;
          color: black !important;
          font-size: 11pt !important;
          text-align: center !important;
          text-shadow: 0 0 0.2em #33CCFF !important;
          border: none !important;
          -moz-box-shadow: inset 0 0 1em #33CCFF !important;
}

#sidebar-splitter {
          background-color: #363636 !important;
          border: none !important;
          min-width: 1px !important;
          max-width: 1px !important;
}

#history-panel > hbox,
#bookmarksPanel > hbox {
          -moz-appearance: none !important;
          background-color: #333333 !important;
          color: lightgray !important;
          -moz-box-shadow: inset 0 0 1em black !important;
          border: none !important;
}

/*TRANSPARENT NAVBAR BUTTONS - http://userstyles.org/styles/43376*/

#PersonalToolbar, #nav-bar {
          background: transparent !important;
          border: none!important
}

.toolbarbutton-1 > .toolbarbutton-menubutton-button,
.toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker,
.toolbarbutton-1 {
          background: none !important;
          box-shadow: none !important;
          border-color: transparent !important;
}

#nav-bar:hover .toolbarbutton-1:not([disabled="true"]):hover .toolbarbutton-icon {
          box-shadow: 0px 0px 3px 3px #33CCFF !important;
          background: #33CCFF !important; 
          border-radius: 4px !important;
          -moz-transition: background-color .4s ease-in,  border-color .2s ease-in,  box-shadow .2s ease-in !important;
}

#PlacesToolbarItems:hover .bookmark-item:hover {
          -moz-appearance: none !important;
          border: none!important;
          color: white!important;
          background: none !important;
          text-shadow: #000000 1px 1px 2px, #000000 -1px 1px 1px, #000000 1px -1px 1px, #000000 -1px -1px 1px !important;
          box-shadow: inset 0px 0px 13px 2px #33CCFF, 0px 0px 1em #33CCFF !important;
          -moz-transition-property: all!important;
          -moz-transition-duration: 0.3s!important;
}

#PersonalToolbar:hover toolbarbutton:hover {
          -moz-appearance: none !important;
          border: none!important;
          box-shadow: 0px 0px 3px 3px #33CCFF !important;
          background: #33CCFF !important; 
          border-radius: 4px !important;
          border-color: transparent !important;
          -moz-transition: background-color .4s ease-in,  border-color .2s ease-in,  box-shadow .2s ease-in !important;
}

/*TRANSPARENT TABS*/

.tabbrowser-tab {
          background: none !important;
          color: #ffffff !important;
          font-size: 0.9em !important;
          border: transparent 1px solid !important;
          padding-left: 4px !important;
          padding-right: 4px !important;
          margin-bottom:-2.2px!important;
          -moz-box-shadow: inset 0px 0px 1em 0px rgba(255,255,255,.5) !important; 
          -moz-border-radius: 6px 6px 0px 0px !important;
}

.tabbrowser-tab:not([selected="true"]):hover {
          background: none !important;
          text-shadow: #000000 1px 1px 2px, #000000 -1px 1px 1px, #000000 1px -1px 1px, #000000 -1px -1px 1px !important;
          box-shadow: inset 0px 0px 13px 2px #33CCFF, 0px 0px 1em #33CCFF !important;
          -moz-transition-property: all!important;
          -moz-transition-duration: 0.3s!important;
          border: #666666 1px solid !important;
}

.tabbrowser-tab[selected="true"] {
          box-shadow: inset 0px 0px 13px 2px #33CCFF, 0px 0px 1em #33CCFF !important;
          text-shadow: #000000 1px 1px 2px, #000000 -1px 1px 1px, #000000 1px -1px 1px, #000000 -1px -1px 1px !important;
          background: -moz-linear-gradient(top, rgba(255,255,255,.75), rgba(84,205,245,.75)  5px, rgba(0,0,0,.75) 20px) !important;
          border: #666666 1px solid !important;
}

.tabbrowser-tab[pinned="true"] {
          padding-bottom:1px!important;
}

.tabbrowser-tab[pinned="false"] {
          padding-bottom:0px!important;
}

.tabs-newtab-button {
          background: none !important;
          border: none !important;
}

#TabsToolbar[tabsontop=true] {
          background: transparent !important;
          margin-top: 0px !important;
}

#TabsToolbar[tabsontop=false] {
          background: transparent !important;
}

#TabsToolbar > toolbarbutton:hover {
          box-shadow: inset 0px 0px 13px 2px #33CCFF, 0px 0px 1em #33CCFF !important;
          border-radius: 4px !important;
          -moz-transition: background-color .4s ease-in,  border-color .2s ease-in,  box-shadow .2s ease-in !important;
}

/*TRANSPARENT URL AND SEARCH BAR - http://userstyles.org/styles/44170*/

#urlbar, #searchbar .searchbar-textbox {
          background-color: transparent !important;
}

#identity-box {
          background: none !important;
          margin-top: 1px !important;
          margin-bottom: 1px !important;
          margin-left: 1px !important;
          padding: 0 !important
}

#identity-icon-labels {
          color: #33FF33 !important;
}

#urlbar:hover { 
          -moz-appearance: none !important; 
          -moz-box-shadow: #33CCFF 0px 0px 10px 4px!important;
}

#searchbar .searchbar-textbox:hover { 
          -moz-appearance: none !important; 
          -moz-box-shadow: lightsalmon 0px 0px 10px 4px!important;
}

#urlbar[focused="true"] { 
          -moz-appearance: none !important; 
          -moz-box-shadow: lightgreen 0px 0px 10px 4px!important;
}

#searchbar .searchbar-textbox[focused="true"] { 
          -moz-appearance: none !important; 
          -moz-box-shadow: lightsalmon 0px 0px 10px 4px!important;
}
