css:
@-moz-document domain('virtualmanager.com') {
}

/* REMOVING "TICKETS BANNER" START */
.standard_sales_pitch.tickets {
display: none; }
/* REMOVING "TICKETS BANNER" END (You must delete the 2 lines above to show the banner again) */

input, select {
  border: 1px solid #508b50;
  background-color: #b7e2b0; }

#status_search {
background-color: #b7e2b0; }

#header > .content, #header .container {
  background-image: url("http://getfree.latestdot.net/images/vmtop.jpg"); }

#header {
  background-image: url("http://getfree.latestdot.net/images/vmtop_bg.gif"); }

#header .content .logo {
  top: 4px;
  left: 26px;  }

#save_tactic_button {
width: 60px;
margin-right: 20px; }


.btn_small, #save_tactic_button {
  background: url("http://getfree.latestdot.net/images/btn_small.png");  }

.btn_large, .controls .button, .sponsor .button, .button.large {
  background: url("http://getfree.latestdot.net/images/btn_large.png");  }

.btn {
 background: url("http://getfree.latestdot.net/images/btn.png");
   }

.controls .button_to .subscribe.btn.toggler {
 background: url("http://getfree.latestdot.net/images/suboff.png"); }

.btn.large.toggler {
 background: url("http://getfree.latestdot.net/images/suboff_l.png"); }

.controls .button_to .subscribe.btn.toggler.on {
 background-image: url("http://getfree.latestdot.net/images/subon.png"); }

.btn.large.toggler.on {
 background: url("http://getfree.latestdot.net/images/subon_l.png"); }

#header .content .misc .account .more {
  background: url("http://getfree.latestdot.net/images/klub_r.png");
  border-left: 1px solid #707070;
 opacity: 0.8;
 }

#header .content .misc .account .name {
  background: url("http://getfree.latestdot.net/images/klub_l.png");
  padding-left: 9px;
 opacity: 0.8;
 }

.box .content .actions li {
 border: 1px solid #C4C4C4;
background-color: #c9dd9d;
}

.box .content .actions li:hover {
 border: 1px solid #C4C4C4;
background-color: #508b50;
}

.unisport_banner, .league_banner, .banner, .center iframe, .col34.center, .status > .center{
display: none; }
