css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("whatismyipaddress.com") 
{

#main 
{font-size: 11px !important;}

.adtop, .adleft, .adright, #adspace, 
#section_content ul.ra,
#head_section,
#foot_section,
#google_ads_frame1
{display: none !important;}

#section_content p
{width: 400px !important;}

#section_content
{margin: 0px !important; 
top: 10px !important; 
font-size: 0px !important;}



form[action="/ip-lookup"]
{position: absolute !important; top: 10px !important; left: -150px !important;  
font-size: 11px !important;}
form[action="/ip-lookup"] + p
{display: none !important;  font-size: 11px !important;}

#section_content h1
{position: absolute !important; top: 60px !important; left: -150px !important; font-size: 13px !important;}
#section_content h1 + p
{position: absolute !important; top: 80px !important; left: -150px !important; font-size: 11px !important;}

#section_content h2[id="General-IP-Information"]
{position: absolute !important; top: 150px !important; left: -150px !important; font-size: 13px !important;}
#section_content h2[id="General-IP-Information"] + form
{position: absolute !important; top: 200px !important; left: -150px !important; font-size: 11px !important;}

#section_content h2[id="Geolocation-Information"]
{position: absolute !important; top: 360px !important; left: -150px !important; font-size: 13px !important;}
#section_content h2[id="Geolocation-Information"] + table
{position: absolute !important; top: 410px !important; left: -150px !important; font-size: 11px !important;}

#section_content h2[id="Comments"]
{position: absolute !important; top: 450px !important; left: 300px !important; font-size: 11px !important;}
#section_content h2[id="Comments"] + form
{position: absolute !important; top: 500px !important; left: 300px !important; font-size: 11px !important;}


#section_content h2[id="Geolocation-Map"]
{position: absolute !important; top: 0px !important; left: 300px !important; font-size: 11px !important;}
#section_content h2[id="Geolocation-Map"] + #map_canvas
{position: absolute !important; top: 50px !important; left: 300px !important; font-size: 11px !important;}



}
