css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://japanese.livejournal.com/") {

body {
background-color: #F2F4F4!important;
color: #0D3649!important;
font-family: cambria, serif!important;
font-size: 12px;}

#lj_controlstrip {display: none!important;}

.header-title {
    background-color: transparent!important;
    color: #0D3649 !important;
    font-size: 500%!important;
    font-weight: bold;
    letter-spacing: -5px;
    margin-top: -55px;
    text-transform: uppercase;
text-align: center!important;
}

.header-menu {
    -moz-border-radius: 50px;
    -moz-box-shadow: 2px 2px 3px #606060;
    background-color: #002B42!important;
    color: #0D3649 !important;
    font-size: 150%!important;
    margin-left: auto!important;
    margin-right: auto!important;
    padding: 7px!important;
    width: 60%;
border: none!important;
text-align: center;}

.bodyheaderblock {display: none;}

a {color: #6DB8BF!important;
text-decoration: none!important;}

a:hover {border-bottom: 1px dashed #6DB8BF;
background-color: transparent!important;}

.entryHolder a{
color: #6DB8BF!important;
border-bottom: 1px dashed #6DB8BF;}


.entryHolder, .commentHolder {
background-color: transparent!important;
color: #0D3649!important;
padding-bottom: 15px;
font-size: 120%!important;}

.commentHolder {border-top-width: 4px!important;}

.commentHeader {
    -moz-border-radius: 30px;
    background-color: #0D3649!important;
    color: #89A2BE;
    font-size: 90%!important;
    font-style: italic;
    line-height: 22px;
    margin-top: 17px !important;
    padding-bottom: 4px!important;
    padding-right: 10px!important;
    padding-top: 4px!important;
    text-align: right!important;
    width: 45%!important;} 

.entryUserinfo, {
margin-top: 5px!important;
}

.entryUserinfo, .commentUserinfo {
background-color: #C6C6C6!important;
padding: 10px!important;
margin-top: 5px!important;
-moz-box-shadow: 2px 2px 3px #606060;
}

.commentUserinfo {margin-top: -40px!important;
margin-left: 20px!important;}

.entryUserinfo a, .commentUserinfo a {border: none!important;}

}
