css:
@-moz-document url-prefix(http://www.shacknews.com/chatty)
{

/* highlight "Shacknews" comments */
#chatty_comments_wrap div.fpauthor_14475
{
    border-top: solid 1px #089dc6 !important;
    background-color: #192635 !important;
}

/* unbold poster names */
#chatty_comments_wrap .oneline_user
{
    font-weight: normal !important;
}

/* remove text shadow on original poster names */
span.author a:link, span.author a:visited, span.author a:active, span.author a:hover
{
    text-shadow: none;
}

/* change white header/footer to black (and fix links) */
#container {color: #ccc !important; background: #000000 !important; } 
div.article-body a:link, 
div.article-body a:visited, 
div.article-body a:active
{ 
    color: #bfa55d; 
    text-decoration: underline
} 

div.article-body a:hover
{ 
    color: #dbbd6b; 
}
.border-grd {background: transparent !important; height: 5px !important; margin: 0 !important; } 

