css:
@-moz-document domain('tumblr.com'), url-prefix('http://tumblr.com/dashboard') {
	/* 
		the code in here only apply to:
			-pages on the domain tumblr.com
			-pages whose URLs start with http://tumblr.com/dashboard
	*/
}

{#dashboard_index{background: url('http://animalprintwallpaper.com/wallpapers/zebra-print-pink.jpg') center right #000000 repeat fixed !important;}
body {background: url('http://animalprintwallpaper.com/wallpapers/zebra-print-pink.jpg') center right #3e3e40 repeat fixed !important;}  *{text-shadow:none !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#3e3e40 !important; opacity:.9;}
ol#posts li.notification{background-color: #3e3e40 !important; border-bottom: 2px solid #A59AA0 !important; color: #000000 !important;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#3e3e40;}
#nav .nav_item.active{background-color:#3e3e40;}
#left_column #posts .post .permalink{background-color:#3e3e40;}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#3e3e40 !important;
background-image:none !important;
border:0px !important}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.notification_type_icon{display:none !important}
#left_column #posts .notification {
background-color:#3e3e40 !important;
color:#666 !important;
border-bottom-color: #eee !important}
#new_post_notice_container{
color:#fff !important}
#new_post_notice_container a{
color:#fff !important}
ol#posts li.post.new_post {
background: #ffffff url('http://i56.tinypic.com/n1cebo.png') center center no-repeat;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}
ol#posts .post.new_post .new_post_label {
display:none !important;
}


}
