css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("unfuddle.com") {

/* Layout */
#account_header {
  margin-top: 26px;
  position: static !important;
}
#wrapper {
  padding-top: 0 !important;
}

/* Hide crap */
#sidebar .ticket_status, #sidebar #send_feedback_action, #sidebar ul.icon_adjust {
  display: none;
}

/* Turn sidebar into dropdown menu */
#sidebar {
  background-color: #111;
  float: none !important;
  left: 0;
  position: fixed !important;
  right: 0;
  top: 0 !important;
  width: auto !important;
  z-index: 9999 !important;
}
#context_sensitive {
  overflow: visible !important;
  padding: 5px 10px !important;
}
#context_sensitive > p,
#sidebar hr {
  display: none;
}
#sidebar h2 {
  color: #aaa !important;
  display: inline-block;
  font-size: 11px !important;
  padding: 0 !important;
  margin: 0 !important;
}
#ticket_reports ul {
  background-color: #111;
  display: inline;
  position: absolute;
}
#ticket_reports li {
  border: 0 !important;
  border-bottom: 1px solid #333 !important;
  display: none;
  font-size: 11px !important;
  left: auto !important;
  margin: 0 0 0.2em !important;
  padding: 0 0.5em 0.2em !important;
}
#ticket_reports li.selected {
  background-color: transparent !important;
  border: 0 !important;
  border-bottom: 1px solid #333 !important;
  display: block;
}
#ticket_reports:hover li {
  display: block;
}
#ticket_reports a {
  text-decoration: none !important;
  color: #ccc !important;
}
#ticket_reports li.selected a {
  color: #fff !important;
}
#ticket_reports a:hover {
  color: #fff !important;
}

#calendar_sidebar #calendars_future {
  display: none;
  position: absolute;
}
#calendar_sidebar:hover #calendars_future {
  display: block;
}


#main {
  border: 0 !important;
  font-size: 90%;
  position: static !important;
  width: auto !important;

  overflow: hidden !important;
}

/* Views */
#tickets_update_heading {
  background-color: transparent !important;
}
.report_container {
  background-color: transparent !important;
}
.report_table_wrapper {
  padding-left: 0 !important;
  padding-right: 0 !important;
}
table.pretty th {
  background-color: #fff !important;
  border: 0 !important;
  font-size: 11px;
  overflow: visible !important;
  padding: 0 3px 4px !important;
  white-space: normal !important;
}
table.pretty td {
  overflow: visible !important;
  vertical-align: top;
  white-space: normal !important;
}
#tickets_report_table td {
  height: auto !important;
  padding: 3px 3px !important;
}
table.pretty input[type=checkbox] {
  margin: 0 0.3em;
}
#report_render .report_table a {
  display: inline !important;
  padding: 0 !important;
}


/* Design */
#header {
  background: #f0f6ff none !important;
}
#header h1 {
  font-size: 20px !important;
  text-shadow: none !important;
}
#header h1 a {
  color: #666 !important;
}
#aux_menu {
  background-color: #889;
  padding: 0.5em !important;
  top: 0 !important;
}

}
