ol#posts li.post.new_post {
background: #ffffff url('http://i1117.photobucket.com/albums/k582/kellisays/greendaynenu4.jpg') center center no-repeat;
}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
