css:
@namespace url(http://www.w3.org/1999/xhtml);

/*33d*/


@-moz-document domain("skully.hopto.org") {
html, body {
  background-color: #000000 !important;
  color: #669 !important;
}

/*Left column of stuff*/
table tbody tr td[width="1%"][valign="top"] 
{

}

/*Middle column of stuff*/
table tbody tr td[width="99%"][valign="top"] 
{

}

/*Right column of stuff*/
body table tbody tr td:nth-child(3)[valign="top"]  /* lol CSS */
{

}


/* Links */
a {
  color: #57d !important;
}

/* Table titles, e.g., [Currently Playing] */
tr [bgcolor="#002e5b"], td [bgcolor="#c0c0c0"], table [bgcolor="#f6f6f6"] tbody tr:first-child{
  background-color: #335 !important;
}

/* The title of the current queue [Artist  Title  Icon, etc] */
.title {
  background-color: #224 !important;
}

/* Text inside titles. MOTD is included because
   lol cbf doing it right */
td b, font {
  color: #bbb !important;
}


/*Inside tables on the right and left*/
table [bgcolor="#f6f6f6"] tbody tr, table [bgcolor="#eeeeee"] {
  background-color: #0f0f0f !important;
}

/*Alternating row in queue*/
.queue [bgcolor="#dadada"], .queue [bgcolor="#dadada"] td {
  background-color: #0a0a0a !important;
  color: #669 !important;
  font-size: 100% !important;
}

/*Other row in queue*/
.queue [bgcolor="#f6f6f6"], .queue [bgcolor="#f6f6f6"] td {
  background-color: #0f0f0f !important;
  color: #669 !important;
  font-size: 100% !important;
}

/*One of the alternating rows. The 98% selector is to make sure it 
only picks the middle table. Ugggggggggggggggghhhhhhh*/
table [width="98%"] tbody [bgcolor='#dadada'] {
  background-color: #0a0a0a !important; 
  height: 50px;
}

table [width="98%"] img[alt="This song has been requested"], table [width="98%"] img[alt="This song was requested"] {
  height: 42px !important;
}

/*The other row*/
table [width="98%"] tbody  [bgcolor='#f6f6f6'] {
  background-color: #0f0f0f !important; 
  height: 50px;
}

/* Text inside playlist/queue */
td [color='#003366'] {
  color: #669 !important;
  font-size: 120% !important;
}


/* [   ( Showing 1 to 25 of 7085 )   ]  */
tr [bgcolor="#e0e0e0"] {
  background-color: #224 !important;
}

.queue tbody tr td {
    color: #669 !important;
}


/* Dedication text */
table [width="100%"] font[color="#464646"] small {
  font-size: 120% !important;
  color: #ff5 !important;
}

/************************************/
/*Song Information window stuff here*/
/************************************/

/*Even rows*/
tr td [bgcolor="#ffffff"], tr td [bgcolor="ffffff"] {
  background-color: #0a0a0a !important;
}

/*Odd rows*/
tr td [bgcolor="eaeaea"], tr td [bgcolor="#eaeaea"]{
  background-color: #0f0f0f !important;
}

/************************************/
    /*Request window stuff here*/
/************************************/

.lasticon {
  color: #669;
}

table [bgcolor="#d4d4d4"]{
  background-color: #000 !important;
}

table [bgcolor="#d4d4d4"] td[align="center"] {
  display: none !important;
}
table [bgcolor="#d4d4d4"] td[valign="top"] {
  background-color: #000 !important;
}

table [bgcolor="#d4d4d4"] td[valign="top"] {
  background-color: #0f0f0f !important;
}

.bodycopy {
  color: #669 !important;
  font-size: 100% !important;
}

table [bgcolor="#004080"]  {
  background-color: #0a0a0a !important;
  font-size: 80% !important;
}

table [bgcolor="#d4d4d4"] td ul li{
  color: #b55 !important;
}

}
