css:
/* All credit goes to bamtam for the design. Initial stylish by ysbreker. Webkit fixes by ovrlrd */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.shacknews.com") {
	div.fullpost { 
		background: #202224 !important;
		-webkit-border-radius: 3px;
		-moz-border-radius: 3px;
		border-radius: 3px;
		border: 0 none !important; 
		padding: 0 !important; 
		margin-bottom: 15px !important; 
		z-index: 10;
	}
	
	div.fpmod_nws .postmeta {
		background-image: -webkit-gradient(linear,left top,left 95,color-stop(0%,#5c191b),color-stop(1, rgb(32,34,36))) !important;
		background-image: -moz-linear-gradient(top, #5c191b 0%, #202224 100%) !important;
	}
	
	div.fpmod_informative .postmeta {
		background-image: -webkit-gradient(linear,left top,left 95,color-stop(0%,#144f76),color-stop(1, rgb(32,34,36))) !important;
		background-image: -moz-linear-gradient(top, #144f76 0%, #202224 100%) !important;
	}

	div.fpmod_stupid .postmeta {
		background-image: -webkit-gradient(linear,left top,left 95,color-stop(0%,#12430f),color-stop(1, rgb(32,34,36))) !important;
		background-image: -moz-linear-gradient(top, #12430f 0%, #202224 100%) !important;
	}
	
	div.fpmod_political .postmeta {
		background-image: -webkit-gradient(linear,left top,left 95,color-stop(0%,#633619),color-stop(1, rgb(32,34,36))) !important;
		background-image: -moz-linear-gradient(top, #633619 0%, #202224 100%) !important;
	}
	
	div.postmeta { 
		-webkit-border-radius: 3px;
		-moz-border-radius: 3px; 
		border-radius: 3px; 
		background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, rgb(61,65,68)),color-stop(1, rgb(32,34,36))) !important;
		background-image: -moz-linear-gradient(top, #3D4144 0%, #202224 100%) !important;
		border-bottom: 1px solid #1a1c1e !important;
		padding: 0 22px !important;
		height: 43px !important;
		margin: 0 !important;
	}

	.author {
		line-height: 43px;
		color: #a5a5a5 !important;
	}
	.author .user a { 
		line-height: 20px; 
		display: inline-block; 
		padding: 0 14px 0 10px !important;
		-webkit-border-radius: 12px;
		-moz-border-radius: 12px; 
		border: 1px solid #1a4863 !important; 
		background: #000 url(data:image/png;base64,) no-repeat 93% 50%; text-shadow: none !important; 
		font-weight: normal !important; 
		text-decoration: none !important;
	}
	
	.postnumber {
		margin: 0 !important;
		width: 225px !important;
		height: 43px !important;
		top: 0 !important;
		right: 100px !important;
		left: auto !important;
		float: none !important;
		position: absolute;
		z-index: 10;
	}
	
	.postnumber a {
		margin: 0 !important;
		width: 225px !important;
		height: 43px !important;
		display: block;
		background-image: none !important;
		background-color: rgba(0,0,0,0);
	}

	div.postbody { 
		border-top: 1px solid #303233 !important;
		margin: 0 !important;
		padding: 1em 23px !important; 
		line-height: 1.5em;
	}

	div.reply { 
		width: auto !important;
		right: 22px !important;
		top: 10px !important;
		color: #7dddff;
	}

	div.reply a { 
		color: #7dddff !important;
		line-height: 20px;
		display: inline-block;
		padding: 0 24px 0 10px !important;
		-webkit-border-radius: 12px;
		-moz-border-radius: 12px;
		border: 1px solid #1a4863 !important;
		background: #000 url(data:image/png;base64,) no-repeat 90% 50% !important; text-shadow: none !important;
		text-decoration: none !important;
		text-transform: none;
	}

	div.postdate {
		color: #a5a5a5 !important;
		padding: 0 !important;
		right: 100px !important;
		top: 0;
		bottom: auto !important;
		font-size: 12px !important;
		line-height: 43px !important;
		height: 43px !important;
		text-align: left !important;
		width: 225px !important;
	}

	.refresh {
		width: 16px !important;
		height: 20px !important;
		margin: 0 !important;
		top: 14px !important;
		right: 325px !important;
		left: auto !important;
	}

	.refresh a { 
		margin: 0 !important;
		background: url(data:image/png;base64,) no-repeat 0 0 !important; 
	}
	
	.refresh a:hover, .refresh a:focus {
		background: url(data:image/png;base64,) no-repeat 0 0 !important;
	}

	div.postmeta a.closepost {
		background: none transparent;
		text-indent: -999em;
		width: 20px !important; 
		height: 20px !important;
		left: -10px !important;
		top: -10px;
		bottom: auto !important;
	}
	
	div.postmeta a.closepost:hover, div.postmeta a.closepost:focus {
		background: url(data:image/png;base64,) no-repeat 0 0;
	}

	div.fullpost:before { 
		content: '';
		width: 50px;
		height: 15px;
		margin: 0;
		background: #202224;
		position: absolute;
		bottom: -15px;
		left: 0;
	}

	div.fullpost:after {
		content: '';
		width: 25px;
		height: 15px;
		-webkit-border-top-right-radius: 15px;
		-moz-border-radius-topright: 15px;
		background: #000;
		position: absolute;
		bottom: -15px;
		left: 0;
	}

	div.postbody:before{
		content: '';
		width: 25px;
		height: 15px;
		-webkit-border-top-left-radius: 15px;
		-moz-border-radius-topleft: 15px;
		background: #000;
		position: absolute;
		bottom: -15px;
		left: 25px;
	}

	div.capcontainer { 
		position: relative;
	}

	.capcontainer {
		margin-left: 18px !important;
	}

	div.capcontainer ul { 
		margin-left: 0 !important;
		background: none !important;
	}

	div.capcontainer ul ul {
		margin-left: 15px !important;
	}
	
	div.capcontainer .sel > ul {
		margin-left: 16px !important;
	}

	div.capcontainer ul ul {
		margin-left: 0 !important;
	}
	
	div.capcontainer .sel > ul {
		margin-left: 16px !important;
	}

	div.capcontainer ul li {
		margin: 0 !important;
		padding: 3px 0 0 20px!important;
		background: url(data:image/png;base64,) no-repeat 0 0,
					url(data:image/png;base64,) repeat-y 0 0,
					#000 !important;
	}

	div.capcontainer ul li.last {
		background: url(data:image/png;base64,) no-repeat 0 0 #000 !important;
	}
	
	span.oneline_user { 
		margin-left: 5px !important;
	}
	
	div.capped div.capcontainer { overflow: visible !important; height: 517px !important; }
	
	div.capped div.capcontainer > ul { height: 517px !important; overflow: hidden !important; }
	
	div.capped p.capnote {
		top: -18px !important;
		left: -18px !important;
		margin: 0;
		border-top: 1px dotted #303233 !important;
		border-bottom: 0 none !important;
		border-left: 0 none !important;
		border-right:  0 none !important;
		background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#141516), color-stop(100%,#202224));
		background-image: -moz-linear-gradient(top, #141516 0%, #202224 100%);
		-webkit-border-bottom-right-radius: 3px;
		-webkit-border-bottom-left-radius: 3px;
		-moz-border-radius-bottomright: 3px;
		-moz-border-radius-bottomleft: 3px;
		padding: 0 18px 0 0 !important;
	}
	div.capped p.capnote a {
		background: none transparent !important;
		height: 32px !important;
		line-height: 32px !important;
		padding: 0 0 0 22px !important;
	}
	
	div.inlinereply {
		top: -15px;
		z-index: 10;
		-webkit-border-bottom-right-radius: 3px;
		-webkit-border-bottom-left-radius: 3px;
		-moz-border-radius-bottomright: 3px;
		-moz-border-radius-bottomleft: 3px;
		border-color: #1a1c1e !important;
	}
	
	div.closeform a {
		right: auto !important;
		background: none transparent;
		text-indent: -999em;
		width: 20px !important; 
		height: 20px !important;
		left: -10px !important;
		top: -12px !important;
	}
	
	div.closeform a:hover, div.closeform a:focus {
		background: url(data:image/png;base64,) no-repeat 0 0;
	}
	
	/* all the mods get the evil eye! */
	div.olauthor_3259 span.oneline_user, div.olauthor_10028 span.oneline_user, div.olauthor_168479 span.oneline_user, div.olauthor_5334 span.oneline_user, div.olauthor_169489 span.oneline_user, div.olauthor_8105 span.oneline_user, div.olauthor_5278 span.oneline_user, div.olauthor_6674 span.oneline_user, div.olauthor_32016 span.oneline_user, div.olauthor_1194 span.oneline_user, div.olauthor_171402 span.oneline_user, div.olauthor_6585 span.oneline_user, div.olauthor_168256 span.oneline_user, div.olauthor_169197 span.oneline_user, div.olauthor_3243 span.oneline_user, div.olauthor_169049 span.oneline_user, div.olauthor_9085 span.oneline_user, div.olauthor_6380 span.oneline_user, div.olauthor_12398 span.oneline_user, div.olauthor_7570 span.oneline_user, div.olauthor_8316 span.oneline_user, div.olauthor_9031 span.oneline_user, div.olauthor_9211 span.oneline_user, div.olauthor_7660 span.oneline_user, div.olauthor_169927 span.oneline_user, div.olauthor_15130 span.oneline_user, div.olauthor_169942 span.oneline_user, div.olauthor_185650 span.oneline_user {
		background-image: url(data:image/png;base64,) !important;
	}
	
	div.oneline a.oneline_user:link, div.oneline a.oneline_user:visited, div.oneline a.oneline_user:active, div.oneline a.oneline_user:hover, div.oneline span.oneline_user {
		color: #ffed92 !important;
		font-weight: normal !important;
	}

	div.postdate .expiration {
		display: block !important;
		position: relative !important;
		width: 60px !important;
		height: 1px !important;
		float: right;
		margin: 16px 0px 0px 7px !important;
		z-index: 21;
	} 
}
