css:
/* All credit goes to ysbreker for his first attempt at this style, here are webkit fixes for it. Note that ysbreaker has not finished it, but I thought I would include webkit fixes so he had less work to do. */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.shacknews.com") {
	
div.fullpost {/* Webkit gradient fix by ovrlrd */background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, rgb(26,72,99)),color-stop(0.35, rgb(32,34,36)));
background-image: -moz-linear-gradient(top, #1A4863 0%, #202224 35%); 
/* Webkit border fix by ovrlrd */
-webkit-border-radius: 3px; border: 0 none !important; 
-moz-border-radius: 3px; border: 0 none !important; padding: 3px 0 40px !important; margin-bottom: 15px !important; }
.postnumber { display: none; }
.fullpost div.postmeta { margin-left: 22px !important; }
.author .user a { line-height: 20px; display: inline-block; padding: 0 14px 0 10px !important; /* Webkit border fix by ovrlrd */ -webkit-border-radius: 12px; border: 1px solid #1a4863 !important; -moz-border-radius: 12px; border: 1px solid #1a4863 !important;background: #000 url(data:image/png;base64,) no-repeat 93% 50%; text-shadow: none !important; font-weight: normal !important; text-decoration: none !important; }
div.postbody { margin-top: 1em !important; line-height: 1.5em; }
div.reply { width: auto !important; right: 22px !important; top: auto !important; bottom: 12px; color: #7dddff; }
div.reply a { color: #7dddff !important; line-height: 20px; display: inline-block; padding: 0 24px 0 10px !important; /* Webkit border fix by ovrlrd */ -webkit-border-radius: 12px; border: 1px solid #1a4863 !important; -moz-border-radius: 12px; border: 1px solid #1a4863 !important; background: #000 url(data:image/png;base64,) no-repeat 90% 50% !important; text-shadow: none !important; text-decoration: none !important; text-transform: none; }
div.postdate { padding: 0 !important; right: 22px !important; top: 3px; bottom: auto !important; font-size: 12px !important; line-height: 22px !important; height: 22px !important; width: 300px !important; }
.refresh { width: 16px !important; height: 20px !important; margin: 0 !important; top: 9px !important; left: 4px !important; }
.refresh a { margin: 0 !important; background: url(data:image/png;base64,) no-repeat 0 0 !important; }
div.postmeta a.closepost { background: url(data:image/png;base64,) no-repeat 0 0; text-indent: -999em; left: 22px !important; }
div.fullpost:before { content: ''; width: 50px; height: 15px; margin: 0; background: #202224; position: absolute; bottom: -15px; left: 0; }
div.fullpost:after { content: ''; width: 25px; height: 15px; /* Webkit border fix by ovrlrd */ -webkit-border-top-right-radius: 15px; -moz-border-radius-topright: 15px; background: #000; position: absolute; bottom: -15px; left: 0; }
div.postbody:before{ content: ''; width: 25px; height: 15px; /* Webkit border fix by ovrlrd */ -webkit-border-top-left-radius: 15px; -moz-border-radius-topleft: 15px; background: #000; position: absolute; bottom: -15px; left: 25px; }
div.capcontainer { position: relative; }
.capcontainer { margin-left: 18px !important; }
.capcontainer .fullpost { 
/* Webkit gradient fix by ovrlrd */
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, rgb(61,65,68)),color-stop(1, rgb(32,34,36)));
background-image: background-image:-moz-linear-gradient(top, #3D4144 0%, #202224 100%) !important; }
div.capcontainer ul ul { margin-left: 23px !important; }
}
