css:
@-moz-document domain('shacknews.com') {

div#chatty_comments_wrap {
background-color: black !important;
}

div.commentsblock{
background-color: white !important;
}



div.fullpost {
background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, #09f),color-stop(0.35, #ddd));
background-image: -moz-linear-gradient(top, #09f 0%, #ddd 35%); 
-webkit-border-radius: 6px; 
border: 0 none !important; 
-moz-border-radius: 6px; 
border: 0 none !important; 
padding: 3px 0 5px !important; 
}

.postnumber { 
float:right !important;
}
.postnumber a{
background-image: url(data:image/png;base64,R0lGODlhZAAUAMIEAElJSf8zmQCZ/66um////////////////yH5BAEKAAcALAAAAABkABQAAANxeLrc/jDKSau9OOvNu/9gKI4hYJ4nWRFs26oUIMz0DMASEez8TuARGUomuAEfuoFS6Ts+iDWb06Hr8X5TBhElzS6qy0HTu4DWjORD1YpNH042tLvla7vLxTs1YNcrTH4NLIGEhYaHiImKi4yNjo+QQAkAOw==) !important;
}

.fullpost div.postmeta { 
margin-left: 22px !important; 
}

div.postmeta span.author {
color: white !important;
display: inline-block;
text-indent: -100px;
width: 600px;
}

div.postmeta span.author * {
text-indent: 100px;
}

.author .user{
color: #F39 !important;
text-shadow: white 1px 1px 2px !important; 
}


.author .user { 
font-weight: bold;
line-height: 20px; 
display: inline-block; 
}

.author .user a{
color: white !important;
text-shadow: #F39 1px 1px 2px !important;
}

div.postbody { 
margin-top: 0.5em !important; 
color: black !important;
}

div.postbody a:link, 
div.postbody a:visited, 
div.postbody a:active {
color: #09f !important;
}
div.postbody a:hover {
color: #f39 !important;
}

a.closepost {
position: absolute;
top: 5px;
left: 5px;
color: white !important;
}

a.closepost:hover{
color: #f39 !important;
}

div.reply { 
right: 1px !important; 
top: auto !important; 
bottom: 1px;
}

div.reply a { 
color: #F39 !important; 
line-height: 20px; 
display: inline-block; 
padding: 0 3px 0 3px !important;  
-webkit-border-radius: 12px; 
border: 1px solid #09f !important; 
-moz-border-radius: 12px; 
border: 1px solid #09f !important; 
text-shadow: white 0px 0px 2px !important; 
text-decoration: none !important; 
text-transform: none; 
}

div.reply a:link, div.reply a:visited, div.reply a:active,
div.fpmod_nws div.reply a:link, div.fpmod_nws div.reply a:visited, div.fpmod_nws div.reply a:active, div.fpmod_nws div.reply a:hover {
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #8df),color-stop(0.5, #09f),color-stop(1, #8df));
background-image: -moz-linear-gradient(top, #8df 0%, #09f 50%, #8df 99%);
}

div.reply a:hover,
div.fpmod_nws div.reply a:hover {
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #09f),color-stop(0.5, #8df),color-stop(1, #09f));
background-image: -moz-linear-gradient(top, #f39 0%, #fad 50%, #f39 99%); 
text-shadow: white 1px 1px 2px !important; 
color: black !important;
text-shadow: #F39 0px 0px 5px !important; 
}


div.postdate { 
padding: 0 !important; 
right: 70px !important; 
top: 3px; 
bottom: auto !important; 
font-size: 12px !important; 
line-height: 22px !important; 
height: 22px !important; 
width: 300px !important; 
color: white !important;
}

.refresh { 
width: 16px !important; 
height: 20px !important; 
margin: 0 !important; 
top: 9px !important; 
left: 20px !important; 

}

.refresh a { 
margin: 0 !important; 
background: url(data:image/png;base64,) no-repeat 0 0 !important; 
}



/*  this section created little 'nubs' that replies come from

div.fullpost {
margin-bottom: 15px !important; 
}

div.fullpost:before { 
content: ''; 
width: 50px; 
height: 15px; 
margin: 0; 
background: #ddd; 
position: absolute; 
bottom: -15px; 
left: 0; 
}

div.fullpost:after { 
content: ''; 
width: 25px; 
height: 15px;  
-webkit-border-top-right-radius: 15px; 
-moz-border-radius-topright: 15px; 
background: white; 
position: absolute; 
bottom: -15px; 
left: 0; 
z-index: 1;
}

div.postbody:before{ 
content: ''; 
width: 25px; 
height: 15px;  
-webkit-border-top-left-radius: 15px; 
-moz-border-radius-topleft: 15px; 
background: white; 
position: absolute; 
bottom: -15px; 
left: 25px; 
z-index: 1;
}

div.fpauthor_14475:before { 
content: ''; 
width: 50px; 
height: 15px; 
margin: 0; 
background: #09f; 
position: absolute; 
bottom: -15px; 
left: 0; 
}

*/



div.capcontainer { 
position: relative;  
margin-left: 18px !important; 
}

div.capcontainer div.fullpost { 
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #bbb),color-stop(1, #ddd))) !important; 
background-image: -moz-linear-gradient(top, #bbb 0%, #ddd 100%) !important; 
}

div.capped p.capnote {
border: none;
-webkit-border-radius: 6px; 
border: 0 none !important; 
-moz-border-radius: 6px; 
border: 0 none !important;
background-color: #bbb !important;
padding: 0px 0px 0px 6px !important;
margin: 0px 0px 0px 4px !important;
width: 98% !important;
}

div.capped p.capnote a {
width: 98%
}

div.capped p.capnote a:link, div.capped p.capnote a:visited, div.capped p.capnote a:active {
background-color: #bbb !important;
color: #333 !important;
}

div.capped p.capnote:hover,
div.capped p.capnote a:hover {
background-color: #ccc !important;
}

div.capcontainer>ul>ul,
div.fullpost + div.oneline + ul { 
margin-left: 23px !important; 
}


div.threads ul ul li.last{
background: white !important;
}


div.pagenavigation {
border: 1px solid #888;
-webkit-border-radius: 12px; 
-moz-border-radius: 12px;
height: 24px;
padding-top: 6px;
margin: 0 30% !important;
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #bbb),color-stop(0.5, #eee),color-stop(1, #ccc)) !important; 
background-image: -moz-linear-gradient(top, #bbb 0%,#eee 50% #ccc 100%) !important;
}

div.commentsblock div.pagenavigation span {
color: black;
}

div.pagenavigation span.nextprev,
div.pagenavigation a{
-webkit-border-radius: 6px; 
-moz-border-radius: 6px;
border: 1.5px solid #7A7A6D !important;
color: #7A7A6D !important;
background-image: -webkit-gradient(linear,left top,left  bottom,color-stop(0, #bbb),color-stop(1, #ddd)) !important; 
background-image: -moz-linear-gradient(top, #bbb 0%, #ddd 100%) !important;
}



div.pagenavigation a.selected_page {
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #bbb),color-stop(0.5, #09f),color-stop(1, #ccc)) !important; 
background-image: -moz-linear-gradient(top, #bbb 0%,#09f 50% #ccc 100%) !important;
color: black !important; 
font-weight: 900 !important;
}

div.pagenavigation a:hover{
background: #f39 !important;
}


div.oneline9 span.oneline_body { color: #888 !important; }
div.oneline8 span.oneline_body { color: #777 !important; }
div.oneline7 span.oneline_body { color: #666 !important; }
div.oneline6 span.oneline_body { color: #555 !important; }
div.oneline5 span.oneline_body { color: #444 !important; }
div.oneline4 span.oneline_body { color: #333 !important; }
div.oneline3 span.oneline_body { color: #222 !important; }
div.oneline2 span.oneline_body { color: #111 !important; }
div.oneline1 span.oneline_body { color: #000 !important; }
div.oneline0 span.oneline_body { color: #111 !important; font-weight: bold; }

div.oneline a.lightningbolt{
background: white !important;
}

div.oneline a.oneline_user:link, div.oneline a.oneline_user:visited, div.oneline a.oneline_user:active, div.oneline a.oneline_user:hover, div.oneline span.oneline_user{
color: #09f !important;
}

div.oneline span.this_user{
color: #f39 !important;
}


div.oneline a{
padding: 0px 5px 0px 5px !important;
}

div.oneline a:hover {
background-color: #f39 !important;
-webkit-border-radius: 12px; 
-moz-border-radius: 12px;
color: #000 !important;
}
div.oneline a:hover span.oneline_body{
color: #000 !important;
}

div.olmod_nws a span.oneline_body {
background-image: url(data:image/gif;base64,R0lGODlhFAASAOMOAP4QEP4gIP0xMf1BQf5QUP1hYf5wcP2Bgf+Pkv6gov+vsf7Awf/P0f7g4P///////yH5BAEKAA8ALAAAAAAUABIAAARD8MlJq704z9Y0Rod3CYFYLQCQeEjbEungtlYDp3hOMJgS5KkAQiQAmhpAwEJ0AAgRPwOR0JEUSprFqqJQmL7gsNgTAQA7) !important;
background-repeat: none;
}
div.olmod_nws a{
background-color: #faa !important;
-webkit-border-radius: 12px; 
-moz-border-radius: 12px;
}

div.olmod_informative a span.oneline_body {
background-image: url(data:image/gif;base64,R0lGODlhFAASAOMPAACZ/wSd/iC3/jG8/ULB/FDH/mLL/HDR/oLV/I/e/6Hi/a/n/8Hs/c/y/+D0/v///yH5BAEKAA8ALAAAAAAUABIAAARD8MlJq704T+c0Toh3DYFYMQCgeEnbFinhtpYDp3heNNgS5KlAQjQAmhxAAEOEAAgTvwOx0JEYBB7GqrJYmL7gsNgTAQA7) !important;
background-repeat: none;
}
div.olmod_informative a{
background-color: #faf !important;
-webkit-border-radius: 12px; 
-moz-border-radius: 12px;
}

div.threads ul ul {
background-image: url(data:image/gif;base64,R0lGODlhAQACAIABAICAgP///yH5BAEKAAEALAAAAAABAAIAAAICBAoAOw==) !important;
background-repeat: repeat-y !important;
background-position: 2px bottom !important;
}


div.threads ul ul li {
background-image: url(data:image/gif;base64,R0lGODlhDAAyAIABAICAgP///yH5BAEKAAEALAAAAAAMADIAAAI3RI4ZpsqNHlwz1iNhbqD7713UtTlimaAWebbsW6mYG9OTPNbwbWs99zPtfENgUahL8o6pYPNSAAA7) !important;
background-repeat: no-repeat !important;
background-position: 2px top !important;
}


div.threads ul ul li.last {
background-image: url(data:image/gif;base64,R0lGODlhDAAJAIABAICAgP///yH5BAEKAAEALAAAAAAMAAkAAAIRRI4ZpsqNHlwz1iNhbqD73xUAOw==) !important;
background-repeat: no-repeat !important;
background-position: 2px top !important;

}



.jt_red          { color: #f00 !important; text-shadow: #444 0px 0px 2px;}
.jt_green        { color: #4f0 !important; text-shadow: #444 0px 0px 2px;}
.jt_pink         { color: #e8b !important; text-shadow: #444 0px 0px 2px;}
.jt_olive        { color: olive; text-shadow: #444 0px 0px 2px;}
.jt_fuchsia      { color: #cfc !important; text-shadow: #444 0px 0px 2px;}
.jt_yellow       { color: #ef0 !important; text-shadow: #444 0px 0px 2px;}
.jt_blue         { color: #08f !important; text-shadow: #444 0px 0px 2px;}
.jt_lime         { color: #cf4 !important; text-shadow: #444 0px 0px 2px;}
.jt_orange       { color: #f90 !important; text-shadow: #444 0px 0px 2px;}
.jt_spoiler      { color: #eee !important; background-color: #eee !important; }

.treecollapse{
display: none;
}

div#newcommentbutton a{
background-image: url(data:image/gif;base64,) !important;
}


div.fpmod_nws {
background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, #a00),color-stop(0.35, #ddd)) !important;
background-image: -moz-linear-gradient(top, #a00 0%, #ddd 35%) !important; 
}

div.fpmod_informative{
background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, #909),color-stop(0.35, #ddd)) !important;
background-image: -moz-linear-gradient(top, #909 0%, #ddd 35%) !important;
}

div.fpmod_offtopic,
div.fpmod_stupid  {
background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, #0a0),color-stop(0.35, #ddd)) !important;
background-image: -moz-linear-gradient(top, #0a0 0%, #ddd 35%) !important; 
}

div.fpmod_political,
div.fpmod_religon  {
background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, #d90),color-stop(0.35, #ddd)) !important;
background-image: -moz-linear-gradient(top, #d90 0%, #ddd 35%) !important; 
}

div.fpauthor_14475 {
background-image: -webkit-gradient(linear,left top,left 95,color-stop(0, #f39),color-stop(0.35, #09f));
background-image: -moz-linear-gradient(top, #f39 0%, #09f 35%);
}
div.fpauthor_14475>div.postbody a {
color: white !important;
}
div.fpauthor_14475>div.postbody a:hover {
color: #f39 !important;
}





div.inlinereply, div.newcommentform {
background: #ccc !important;
border-color: #f39 !important;
-webkit-border-radius: 12px; 
-moz-border-radius: 12px;
margin-top: 3px;
}
div.postbox input, div.postbox textarea, div.postbox button {
background-color: white !important;
border: 1px solid #09f !important;
}
div.postbox div.loggedin span{
color: black !important;
}
div.postbox button.useraction, div#commentssettings button.useraction {
color: black !important;
}
div.postbox textarea {
color: black !important;
}
div#shacktags_legend{
color: black;
}

div.newcommentform {
width: 850px !important;
}

div#commentssettings{
background: #ccc !important;
}
div.closeform a:link, div.closeform a:visited, div.closeform a:active{
color: #888 !important;
}
div#postbox div.closeform a:hover{
color: #09f !important;
}
div#commentssettings div.closeform a:hover {
color: #f39 !important;
}
div#commentssettings h5{
color: black !important;
}
div.interiorform div.control{
color: black !important;
}
div#commentssettings div.control button{
background: white !important;
}


.countdown-wrap {
height: 8px;
width: 48px;
position: absolute;
top: 10px;
right: 0px;
margin: 0px 18px;
background-color: #ddd;
-webkit-border-radius: 4px; 
-moz-border-radius: 4px;
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0, #aaa),color-stop(0.4, #ccc),color-stop(0.6, #ccc),color-stop(1, #eee));
background-image: -moz-linear-gradient(top, #aaa 0%, #ccc 40%, #ccc 60%, #eee 99%);
}
.countdown-value {
-webkit-border-radius: 4px; 
-moz-border-radius: 4px;
}

}
