css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://browser/content/browser.xul)
{

#appmenu-button .button-text,
#appmenu-button dropmarker
{
	visibility: collapse !important;
}

#appmenu-button-container,
#appmenu-button
{
	min-height: 20px !important;
	max-height: 20px !important;
}

[sizemode=normal] #appmenu-button-container
{
	margin-left: -3px !important;
}

[sizemode=maximized] #appmenu-button-container,
[sizemode=maximized] #appmenu-button
{
	min-height: 19px !important;
	max-height: 19px !important;
}

#appmenu-button
{
	margin: 0 0 0 2px !important;
	padding: 0 !important;
	box-shadow: none !important;
	min-width: 0 !important;
	width: 33px !important;
	border-radius: 0 0 5px 5px !important;
	border: 3px solid !important;
	-moz-border-bottom-colors:
		rgba(255, 255, 255, .5) rgba(0, 0, 0, .4) rgba(255, 255, 255, .4) !important;
	-moz-border-right-colors:
		rgba(255, 255, 255, .5) rgba(0, 0, 0, .4) rgba(255, 255, 255, .4) !important;
	-moz-border-left-colors:
		rgba(255, 255, 255, .5) rgba(0, 0, 0, .4) rgba(255, 255, 255, .4) !important;
	border-top: none !important;
	list-style-image: url("data:image/png;base64,") !important;
	background:
		-moz-linear-gradient(
			rgba(255, 255, 255, .6),
			rgba(255, 255, 255, .6) 5px,
			transparent 5px,
			transparent
			) padding-box,
		-moz-radial-gradient(bottom, ellipse farthest-corner,
			rgb(255, 255, 255),
			rgba(255, 255, 255, 0),
			transparent
			) padding-box
		!important;
}

[sizemode=maximized] #appmenu-button
{
	background:
		-moz-linear-gradient(
			rgba(255, 255, 255, .6),
			rgba(255, 255, 255, .6) 4px,
			transparent 4px,
			transparent
			) padding-box,
		-moz-radial-gradient(bottom, ellipse farthest-corner,
			rgb(255, 255, 255),
			rgba(255, 255, 255, 0),
			transparent
			) padding-box
		!important;
}

#appmenu-button:-moz-window-inactive:not(:hover):not([open])
{
	background: none !important;
	box-shadow: none !important;
}

#appmenu-button:hover
{
	background-color: rgb(96, 96, 96) !important;
}

#appmenu-button[open]
{
	border-radius: 0 !important;	
	background:
		-moz-linear-gradient(
			rgb(192, 192, 192),
			rgb(128, 128, 128) 5px,
			rgb(0, 0, 0) 5px,
			rgb(0, 0, 0)
			) padding-box,
		-moz-radial-gradient(bottom, ellipse farthest-corner,
			rgb(255, 255, 255),
			rgba(255, 255, 255, 0),
			transparent
			) padding-box
		!important;
}

[sizemode=maximized] #appmenu-button[open]
{
	background:
		-moz-linear-gradient(
			rgb(192, 192, 192),
			rgb(128, 128, 128) 4px,
			rgb(0, 0, 0) 4px,
			rgb(0, 0, 0)
			) padding-box,
		-moz-radial-gradient(bottom, ellipse farthest-corner,
			rgb(255, 255, 255),
			rgba(255, 255, 255, 0),
			transparent
			) padding-box
		!important;
}

}
