css:
/** 
Comments by vINyL3 Theme by Denwil
**/


/** cs-manager.com only: **/
@-moz-document domain(cs-manager.com) {

#header {
	margin: 0 auto !important;
}
#container, #footer_container,.post,#content {
	margin: 0 auto !important;
}


body 

{	background: #4B493B nonerepeat-y fixed center !important;
}
a {
	font-style: normal !important;
}

/** Add remote logo as bg and change colors of header **/
#header {
	background-color: #9B977A !important;
	background-image: none !important;
	background-repeat: no-repeat !important;
	border-right: none;
	border-bottom: none;
	border-left: none;
	border-top: none;
}


/** Hide original logo: **/
#header img {	visibility: hidden !important;} /** ingame **/
#header a img {	visibility: hidden !important;} /** front page **/


/** box containing it all and a few elements, that shouldnt have any specific colour **/
#container, #footer_container,.post,#content {
	color: #000000 !important;
	background: #ffffff !important;
	border-right: none;
	border-bottom: none;
	border-left: none;
	border-top: none;
}

/** adding borders to some tables **/ 
table.rank,table.login {
	border: 1px solid #000000 !important; 
}
/** adding background to some tables **/ 
table.rank,table.login,table.mail,table.info {
	background: #FFFFFF !important;
}
/** All sorts of 'small' elements needing colour **/
td.space,div.space {
	background: #ffffff !important;
	border: none;
}
a.player,td.player,td.top,td.tactic,a.tactic,a.lineup {
	background: #FFFFFF !important;
	color: #000000 !important;
}
a.tactic:hover,a.lineup:hover {
	background: #EEEEED !important;
	color: #000000 !important;
}
td.position {
	background: #EEEEED !important;
	color: #000000 !important;
}

/** Adding borders and bg-colour to headers **/
td.head, h3 , h2  {
	color: #ffffff !important;
	background-image: none !important;
	border-top: 0px solid #000000 !important;
	border-left: 1px solid #000000 !important;
	border-right: 0px solid #000000 !important;
	border-bottom: 0px solid #000000 !important;
}

/** All sorts of links. (tabs, menu etc) **/
li a {
	background: #E8E8E8 !important;
	color: #000000 !important;
	font-style: normal !important;
}


/** menu specific borders **/
ul#menu li a {
	border-bottom: none;
	border-right: none;
	border-left: none;
	border-top: none;
}

/** tab-specific borders **/
ul#tab li a {
	border: 1px solid #000000 !important;
}

li a:hover {
	background: #9E977A !important;
	color: #000000 !important;
	font-style: normal !important;
}

/** Selected tabs **/
ul#tab li span {
	background: #C5BC57 !important;
	color: #000000 !important;
	font-style: normal !important;
	border: 1px solid #000000 !important;
}

/** other spans (eg online in freinds list) **/
li span {
	color: #42526E !important;
}

/** color of areas surrounding maillist **/
table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #Ffffff !important;
	color: #000000 !important;
}

/** dark area below the left and right menus **/
#menu, #footer_left, #footer_right, div#container>div#info {
	background: #9B977A !important;
}

/** search area **/
form#search {
	border-bottom: none;
	background: #E8E8E8 !important;
}

/**input fields and buttons **/
input,select,textarea,radio {
	background: #CCCCCC !important;
	border: 1px solid #000000 !important;
	color: #000000 !important;
}
.buttonsmall, .button {
	background: #A9A47A !important;
	color: #000000 !important;
	border-top: 1px solid #000000 !important;
	border-left: 1px solid #000000 !important;
	border-right: 1px solid #000000 !important;
	border-bottom: 1px solid #000000 !important;
}

/** news items (community news) **/
td.value {
	background: #FFFFFF !important;
	color: #000000 !important;
}
td.foot,td.info {
	background: #FFFFFF !important;
	color: #000000 !important;
}

/** Training room colours **/
#training_map,#training_player,#skill,#trainer,#training_players {
	background: #E8E8E8 !important;
}
div#training_type {
	background: #E8E8E8 !important;
}

/** FORUM **/
tr.even {
	background: #E8E8E8 !important;
}
tr.odd {
	background: #ffffff !important;
}
div.modify {
	background: #ffffff !important;
}


/** Status messages **/
#ok_msg {
	color: #59F559 !important;
	background-color: #005D00 !important;
	border-top: 2px solid #008B00 !important;
	border-left: 2px solid #008B00 !important;
	border-right: 2px solid #002E00 !important;
	border-bottom: 2px solid #002E00 !important;
}
#error_msg {
	color: #F55959 !important;
	background-color: #5D0000 !important;
	border-top: 2px solid #8B0000 !important;
	border-left: 2px solid #8B0000 !important;
	border-right: 2px solid #2E0000 !important;
	border-bottom: 2px solid #2E0000 !important;
}

/** HELP TAB **/
#help_tab {
	background: #900000 !important;
	color: #FFFFFF !important;
	font-weight: bolder !important;
}

/** LanCamp Training **/
tr.lan td.info {
	background: #ADBDDE !important;
}
tr.lan td.value {
	background: #E1E7F2 !important;
}

/** League table **/
table.table tr.promotion td, table.table tr.promotion_line td {
	color: #00B900 !important;
}
table.table tr.qualup td, table.table tr.qualup_line td {
	color: #000000 !important;
	color: #007400 !important;
}
table.table tr.qualdown td, table.table tr.qualdown_line td {
	color: #000000 !important;
	color: #740000 !important;
}
table.table tr.relegation td, table.table tr.relegation_line td {
	color: #B90000 !important;
}

/** PCW Calendar **/
table.calender td.week {
	background: #FFFFFF !important;
	color: #000000 !important;
}
a.challenge {
	background: #FFFFFF !important;
}
a.challenge:hover {
	background: #EEEEED !important;
}
div.challenge {
	background: #EEEEED !important;
}
div.disabled {
	background-color: #EEEEED !important;
}
}
