css:
/*
* Fancy modal boxes
*  http://userstyles.org/styles/44672
* Created by AndreasSE
*  http://userstyles.org/users/58590
*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tabmodalprompt {
 background: -moz-radial-gradient(rgba(191,191,191,0.5), rgba(0,0,0,0.5)) !important;
}
tabmodalprompt .mainContainer {
 background: transparent !important;
 border: groove 2px lightgray !important;
 border-radius: 10px !important;
 color: black !important;
}
tabmodalprompt .topContainer {
 background: rgba(242,242,242,0.95) !important;
 border-radius: 8px 8px 0px 0px;
}
tabmodalprompt .buttonContainer {
 background: rgba(204,204,204,0.9) !important;
 border-radius: 0px 0px 8px 8px;
}
