css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") { 
    /* star code */
    body {
       background: #FBFBFB!important;
       color: #000!important;
       font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif!important; }
    a {
       color: #2261a0;
       outline: none !important; }
    a:hover {   text-decoration: none !important }
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {   color: #000!important }
    textarea,
    .inputtext,
    .inputpassword {   font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif!important }
    #headNav {
       background-color: #3B5998!important;
       border: none!important; }
    button,
    input,
    textarea,
    .uiButtonText {   font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif!important }
    .uiStream .uiStreamMessage {   font-size: 12px !important }
    #leftCol,
    #left_column {   border-right: 1px solid #BABABA!important }
    .uiSideNav .item:hover,
    .uiSideNav .item:active,
    .uiSideNav .item:focus,
    .uiSideNav .subitem:hover,
    .uiSideNav .subitem:active,
    .uiSideNav .subitem:focus {
       background-color: #ECEFF6!important;
       color: #000!important;
       filter: dropshadow(color=#aaa, offx=1, offy=1);
       text-decoration: none;
       text-shadow: 1px 1px 1px #aaa; }
    .uiHeader,
    .uiHeaderTopBorder,
    .uiHeaderNav,
    .uiHeaderNavEmpty {
       border-color: #BABABA!important;
       margin: 8px 0 0 6px;
       padding: 7px 6px 3px 5px; }
    .uiHeader h2 {
       filter: dropshadow(color=#BABABA, offx=1, offy=1);
       font-size: 12px!important;
       font-weight: 400;
       text-shadow: 1px 1px 1px #BABABA; }
    .uiHeader h3,
    .uiHeader h4 {
       color: #000;
       filter: dropshadow(color=#aaa, offx=1, offy=1);
       font-size: 11px;
       font-weight: 400;
       text-shadow: 1px 1px 1px #aaa; }
    .uiSideNav .loading ul .count,
    .uiSideNav .count {   color: gray }
    .uiSideNav .selectedItem .item,
    .uiSideNav .selectedItem .item:hover,
    .uiSideNav ul .selectedItem .subitem,
    .uiSideNav ul .selectedItem .subitem:hover {
       background-color: #ECEFF6!important;
       color: #000!important;
       filter: dropshadow(color=#aaa, offx=1, offy=1);
       font-weight: 400!important;
       text-shadow: 1px 1px 1px #aaa; }
    .uiHeaderSection,
    .uiSideHeader {
       background-color: #f7f7f7!important;
       border-bottom: 1px solid #BABABA!important;
       border-top: 1px solid #BABABA!important;
       margin-bottom: 10px!important;
       padding: 4px 5px 5px; }
    #fbDockChatBuddylistNub .fbNubFlyoutBody {
       padding-bottom: 10px;
       padding-top: 10px;
       position: relative; }
    .uiComposerOpen .focus_target {   padding-bottom: 20px!important }
    .uiComposerHideContent .child_was_focused,
    .uiComposerHideInput .focus_target {   border-bottom: 1px solid #BABABA!important }
    .uiUfi .ufiItem {
       background-color: #f7f7f7!important;
       border-bottom: 0!important;
       font-size: 12px!important;
       margin-bottom: 5px;
       margin-top: 2px;
       padding: 5px; }
    #fbf-bookmarks ul {
       background: none repeat scroll 0 0 #fff!important;
       border: 1px solid #ccc!important; }
    #fbf-bookmarks ul a {   color: #000!important }
    #fbf-bookmarks ul a:hover {
       background: none repeat scroll 0 0 #ECEFF6!important;
       border-bottom: 1px solid #BABABA!important;
       border-top: 1px solid #BABABA!important;
       color: #000!important;
       filter: dropshadow(color=#aaa, offx=1, offy=1);
       padding: 3px 10px 4px;
       text-shadow: 1px 1px 1px #aaa; }
    #navAccount ul {   border: 1px solid #BABABA!important }
    #navAccount ul a,
    #navAccount ul .logoutButton input {   color: #000!important }
    #navAccount ul a:hover,
    #navAccount ul a:focus,
    #navAccount ul a:active,
    #navAccount .logoutButton:hover input,
    #navAccount .logoutButton input:active,
    #navAccount .logoutButton input:focus {
       background: none repeat scroll 0 0 #ECEFF6!important;
       border-bottom: 1px solid #BABABA!important;
       border-top: 1px solid #BABABA!important;
       color: #000;
       filter: dropshadow(color=#aaa, offx=1, offy=1);
       text-shadow: 1px 1px 1px #aaa; }
    .fcg {   color: #000!important }
    .fbNubButton {
       border-color: #BABABA!important;
       border-style: solid;
       border-width: 1px 0 0 1px;
       color: #000000;
       filter: dropshadow(color=#aaa, offx=1, offy=1);
       font-weight: 400!important;
       padding: 4px 5px;
       text-shadow: 1px 1px 1px #aaa; }
    .fbNub .fbNubButton .UIImageBlock_Content {   line-height: 1.2em }
    #jewelCase .jewelBox {
       background-color: #fff;
       border-color: #ccc #ccc #ccc!important;
       border-style: solid;
       border-width: 1px 1px 2px; }
    #jewelCase .jewelHighlightItem li a:hover,
    #jewelCase .jewelHighlightItem li a:active,
    #jewelCase .jewelHighlightItem li a:focus {
       border-bottom: 1px solid #ECEFF6!important;
       border-top: 1px solid #ECEFF6!important; }
    div.filterListSelector .uiSelectorButton:hover .uiButtonText {   text-decoration: none!important }
    .fbx_stream_header div.filterListSelector .uiSelectorButton,
    .fbx_stream_header div.filterListSelector .uiSelectorButton:active,
    .fbx_stream_header div.filterListSelector .uiSelectorButton:focus,
    .fbx_stream_header div.filterListSelector .uiSelectorButton:hover {   padding: 1px 20px 3px 0!important }
    .fbx_stream_header div.filterListSelector .uiSelectorButton .uiButtonText {
       color: #000!important;
       font-size: 12px!important; }
    .uiMenu {   border-color: #ccc #ccc #ccc!important }
    .uiSideNavCount {   background-color: #2261a0!important }
    .uiSideNavCount .countValue {   color: #fff!important }
    .ginormousProfileName {
       color: #556!important;
       font-size: 20px;
       text-shadow: 1px 1px 1px #889; }
    body.fbChatPopout {   background: none no-repeat scroll center center #fff!important }
    .fbChatPopoutBuddyList .viewContainer {   background-color: #fbfbfb!important }
    .fbChatBuddyList a.friend,
    #pagelet_friends_online .chatOnline {
       color: #2261a0!important;
       font-weight: 400!important; }
    #photoborder {   background: none repeat scroll 0 0 #fbfbfb!important }
    .uiMediaThumb {   border: 1px solid #BABABA!important }
    .uiMediaThumb i {   background-color: #fff!important }
    .uiMediaThumbWrap {   border: 1px solid #BABABA!important }
    /* end code */
}
