css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kingdomofloathing.com") {

body {background: #123 !important;}

*[style*="blue"], *[bgcolor="blue"], 
*[style*="border: 1px solid black"],
#body, div.header, 
#tabs li:not([class="active"]), #tabs ul
{ background: #369 !important;
  border-top-color: #369 !important;
  border-color: #399 !important;}

table, span, center, 
div[id], p, blockquote,
form[name="grafform"]
{ background: #123 !important;
  border-radius: 3px;}

input[class$="button"] 
{ color: #123 !important;
  background: white !important;
  border-color: #399 !important;
  -moz-transition: .5s ease all;
  border-radius: 3px;}
input[class$="button"]:hover { background: #9cf !important;} 
input[class$="button"]:active 
{ background: #69c !important;
  color: white !important;
  border-color: #9cf !important;
  -moz-transition: .2s ease all;
} 
td[rowspan="2"][colspan="3"][height="200"][width="300"],
td[width="100"][height="100"] {background: white !important;}

#tabs li.active {
  border-left-width: 4px !important;
  margin-left: -3px !important;
  border-color: #399 #369 !important;
  border-right: none !important;
  border-radius: 3px 0 0 3px;
  background: #123 !important;
}
p {padding-left: 2px;}

table, body, .black,
 .small, font[color="black"], a
{ color: ghostwhite !important;} 

#ChatWindow b >a[href^="showplayer.php?who="] >font[color="black"]
{ color: antiquewhite !important;}
font[color="green"] {color: limegreen !important;}
font[color="green"] >b:hover,
b[style*="green"]:hover { text-shadow: 1px -1px 0 lightgreen;}
font[color="#000066"],
font[color="blue"] {color: dodgerblue !important;}
font[color="#339966"] {color: springgreen !important;}
font[color="#9933CC"] {color: MediumOrchid !important;}
/*more to come*/

}

@-moz-document url("http://www.kingdomofloathing.com/skills.php?tiny=1"),
               url("http://www1.kingdomofloathing.com/skills.php?tiny=1"),
               url("http://www2.kingdomofloathing.com/skills.php?tiny=1"),
               url("http://www3.kingdomofloathing.com/skills.php?tiny=1"),
               url("http://www4.kingdomofloathing.com/skills.php?tiny=1"),
               url("http://www5.kingdomofloathing.com/skills.php?tiny=1"),
               url("http://www6.kingdomofloathing.com/skills.php?tiny=1") 
{ html {background-color: #123 !important; }
}
