css:
@-moz-document domain("therpf.com"), domain("thereplicapropforum.com") {
	.page {
		width: 100% !important
	}
	body {
		max-width: 100% !important
	}
	span.threadlastpost {
		float: right !important;
		margin-right: 25px !important;
	}
	span.threadstats {
   		position: absolute !important;
	   	right: 0 !important;
	    padding-right: 147px !important;
	}
	ul.threadstats {
		position: absolute !important;
		z-index: 0 !important;
		right: 0 !important;
		padding-right: 139px !important;
		width: 105px !important;
	}
	dl.threadlastpost {
		position: relative !important;
		z-index: 1 !important;
	}
}

@-moz-document url-prefix("http://www.therpf.com/search.php"), url-prefix("http://therpf.com/search.php"), url-prefix("http://www.thereplicapropforum.com/search.php"), url-prefix("http://thereplicapropforum.com/search.php") {
	dl.threadlastpost {
	  	float: left !important;
	}
}
