css:
/*****	Ashun's Shacknews Chatty  */





/*****  THIS STYLING ONLY APPLIES TO THE CHATTY THREAD  */

	@-moz-document url-prefix(http://www.shacknews.com/chatty) {



	/*****  CHATTY HEADER, COMMENTS BLOCK, AND FOOTER  */

		#logo {

			left: -22px 										!important;

			}

		ul.main-nav {

			left: -25px 										!important;

			}

		ul.main-nav li a:link,
		ul.main-nav li a:visited,
		ul.main-nav li a:active {

			color: white 										!important;
			font-family : Calibri 									!important;
			font-size : 18px 									!important;
			font-weight : bold 									!important;
			text-transform: uppercase								!important;
			text-shadow : #00A2F2 0px 0px 6px 							!important;
			letter-spacing: 0px 									!important;
			width: auto 										!important;
			padding: 0px 15px 0px 15px 								!important;
			margin: 0px -4px 0px 2px 								!important;

			}

		ul.main-nav li a:hover {

			color: #09F 										!important;

			}

		ul.main-nav li a.on {

			color: #FF295E 										!important;
			text-shadow : #FF295E 0px 0px 6px 							!important;
			}

		.wrap {

			margin: 0px 										!important;
			width: auto 										!important;

			}

		.wrap a:link,
		.wrap a:visited,
		.wrap a:active {

			color: white 										!important;

			font-family: calibri									!important;
			font-variant: small-caps								!important;
			font-size: 18px 									!important;
			font-weight: 400 									!important;
			text-shadow : black 0px 0px 6px 							!important;
			text-decoration: none 									!important;

			}

		.wrap a:hover {

			color: #09F 										!important;

			}

		.user {

			font-family: calibri									!important;
			font-variant: small-caps								!important;
			font-size: 18px 									!important;
			color: #66CCFF 										!important;
			text-shadow : #00A2F2 0px 0px 6px 							!important;

			}

		#user .search {

			right: 10px 										!important; 
			top: 38px 										!important;

			}


		#container {

			padding: 0px 0px 0px 0px 								!important;
			background: black 									!important;
			margin: 81px 0px 0px 0px								!important;
			border-width: 4px 0px 4px 0px 								!important;
			border-style: ridge 									!important;
			border-color: #FF295E 									!important;

			}

		body.page-chatty #content {

			background: #262420 									!important;

			}

		.border-grd {

			display: none 										!important;
			background: #262420 									!important;
			height: 18px 										!important;

			}

		#chatty_intro_wrap {

			margin: 0px 30px 0px 36px 								!important;
			padding: 55px 36px 7px 30px 								!important;
			width: auto 										!important;

			}

		div.commentsblock {

			background: black 									!important;
			padding: 25px 30px 30px 30px								!important;
			margin-bottom: -18px									!important;
			border-width: 4px 0px 0px 0px 								!important;
			border-style: ridge 									!important;
			border-color: #FF295E 									!important;

			}

		#footer {

			font-family: calibri									!important;
			font-variant: small-caps								!important;
			font-weight: bold 									!important;
			margin: 0px 0px 0px 0px 								!important;
			border-top: 4px dashed #444444 								!important;
			background-color: #262420 								!important;
			border: 0px solid green 								!important;
			height: 140px 										!important;

			}

		#footer-logos {

			display: block 										!important;
			position: relative 									!important;
			top: 30px;
			width: auto;
			height: 27px;
			background-image: url(http://chattypics.com/files/logos_s6t38zj04u.png);
			background-repeat: no-repeat;
			background-position: center;
			
			}

		.logo-wrap {
		
			display: none 										!important;
			
			}
			
	/*****  NEWS BLOCK  */

		/*****  PAGE ADS / ADVERTISEMENTS  

			#superleader {

				display: none 									!important;

				}

			.ad-unit-mpu {

				display: none 									!important;

				}
				
		*/

		#main {

			width: auto 										!important;

			}

		div.article-title {

			margin-top: -25px 									!important;
			font-weight: bold 									!important;
			font-size: 30px 									!important;
			color: white 										!important;

			}

		div.article-author {

			font-size: 11px 									!important;
			font-weight: bold 									!important;
			color: #FF295E										!important;
	
			}

		div.article-post-date {

			font-size: 11px 									!important;
			color: #A19D97										!important;
			font-weight: normal									!important;

			}

		div.article-body {

			padding: 0px 0px 0px 0px 								!important;
			color: white 										!important;
			width: auto 										!important;
			border: 0px solid green 								!important;
			font-size: 16px 									!important;

			}

		div.article-body a:link,
		div.article-body a:visited,
		div.article-body a:active {

			color: #A19D97 										!important;
			font-weight: normal 									!important;
			text-decoration: underline 								!important;

			}

		div.article-body a:hover {

			color: #09F 										!important;

			}

	/*****  NEW COMMENT AND PAGE NAVIGATION BLOCK  */

		div#commenttools.commentstools {

			border: 0px solid black 								!important;
			padding: 5px 0px 0px 0px 								!important;
			margin: 5px 0px -20px 0px 								!important;

			}
			
		.pagenavigation {

			margin-top: 20px									!important;

			}

	/*****  "THREADS | COMMENTS | FILTERS" LINKS  */

		div#chatty_settings {

			left: 0px 										!important;
			padding: 0px 0px 0px 0px 								!important;
			margin: -32px 0px 0px 0px 								!important;
			height: auto 										!important;
			width: auto 										!important;
			border: 0px solid yellow 								!important;
			font-size: 18px 									!important;
			font-family: Calibri 									!important;
			font-variant: small-caps 								!important;
			/*text-shadow : white 0px 0px 6px 							!important;*/
			text-align: center 									!important;

			}

		div#chatty_settings a:link,
		div#chatty_settings a:visited,
		div#chatty_settings a:active {

			color: #A19D97 										!important;
			font-weight: normal 									!important;
			text-decoration: none 									!important;

			}

		div#chatty_settings a:hover {

			color: #09F 										!important;

			}

		div#chatty_settings span.pipe {

			padding: 0px 8px 0px 8px 								!important;
			margin: 0px 0px 0px 0px 								!important;
			border:0px solid red 									!important;
			font-size: 16px 									!important;
			color:#FF295E 										!important;
			vertical-align: top 									!important;

			}

	/*****  "NEW COMMENT | BACK TO TOP" LINKS  */

		div.commentnav {

			color: #FF295E 										!important;
			padding: 0px 										!important;
			margin: 12px 0px 0px 0px 								!important;
			font-size: 16px 									!important;
			font-family: lucida console 								!important;
			/*text-shadow : white 0px 0px 6px 							!important;*/
			letter-spacing: 1px 									!important;

			}

		div.commentnav a:link,
		div.commentnav a:visited,
		div.commentnav a:active {

			color: #A19D97 										!important;
			font-size: 18px 									!important;
			font-family: Calibri 									!important;
			font-variant: small-caps 								!important;
			letter-spacing: normal 									!important;

			}

		div.commentnav a:hover {

			color: #09F 										!important;
			font-family: Calibri 									!important;
			font-variant: small-caps 								!important;
			text-decoration: none 									!important;

			}

	/*****  NEW COMMENT BUTTON  */

		div#newcommentbutton a:link,
		div#newcommentbutton a:visited,
		div#newcommentbutton a:active {

			height: 24px 										!important;
			width: 150px 										!important;
			line-height: 21px 									!important;
			padding: 0px 0px 									!important;
			margin: 12px 0px 0px -3px 								!important;
			color       : white 									!important;
			font-family : Calibri 									!important;
			font-size   : 18px 									!important;
			font-weight : 400 									!important;
			font-variant: small-caps								!important;
			text-shadow : #D4B0C0 0px 0px 6px 							!important;
			background: -moz-linear-gradient(top, #403C36, #3B3731)					!important;
			background: -webkit-linear-gradient(top, #403C36, #3B3731)				!important;
			border: 1px solid black 								!important;
			text-align: center									!important;
			text-indent: 0 										!important;

			}

		div#newcommentbutton a:hover {

			background: -moz-linear-gradient(top, #104661, #0e4059)					!important;
			background: -webkit-linear-gradient(top, #104661, #0e4059)				!important;
			text-decoration:none 									!important;
			border: 1px solid #09F 									!important;

			}

	/***** INACTIVE PREVIOUS BUTTON  */

		div.commentsblock div.pagenavigation span.nextprev {

			background: -moz-linear-gradient(top, #403C36, #3B3731)					!important;
			background: -webkit-linear-gradient(top, #403C36, #3B3731)				!important;
			border: 1px solid black 								!important;
			padding: 1px 10px 									!important;
			margin: 0px 										!important;
			color       : #7A756A 									!important;
			font-family : Calibri 									!important;
			font-size   : 18px 									!important;
			font-weight : 400 									!important;
			font-variant: small-caps								!important;
			text-shadow : #5C5851 0px 0px 6px 							!important;

			}

	/***** CURRENT PAGE NUMBER BUTTON  */

		div.commentsblock div.pagenavigation a.selected_page:link,
		div.commentsblock div.pagenavigation a.selected_page:visited,
		div.commentsblock div.pagenavigation a.selected_page:active {

			background: -moz-linear-gradient(top, #701d2b, #681a27)					!important;
			background: -webkit-linear-gradient(top, #701d2b, #681a27)				!important;
			border: 1px solid #FF295E 								!important;
			padding: 1px 10px 									!important;
			margin: 0px 										!important;
			color       : white 									!important;
			font-family : Calibri 									!important;
			font-size   : 18px 									!important;
			font-weight : 400 									!important;
			font-variant: small-caps								!important;

			}

	/***** UNSELECTED PAGE BUTTONS  */

		div.commentsblock div.pagenavigation a:link,
		div.commentsblock div.pagenavigation a:visited,
		div.commentsblock div.pagenavigation a:active {

			background: -moz-linear-gradient(top, #403C36, #3B3731)					!important;
			background: -webkit-linear-gradient(top, #403C36, #3B3731)				!important;
			border: 1px solid black 								!important;
			padding: 1px 10px 									!important;
			margin: -1px 										!important;
			color       : white 									!important;
			font-family : Calibri 									!important;
			font-size   : 18px 									!important;
			font-weight : 400 									!important;
			font-variant: small-caps								!important;
			text-shadow : #D4B0C0 0px 0px 6px 							!important;

			}

		div.commentsblock div.pagenavigation a:hover {

			background: -moz-linear-gradient(top, #104661, #0e4059)					!important;
			background: -webkit-linear-gradient(top, #104661, #0e4059)				!important;
			border: 1px solid #09F 									!important;

			}



		}





/*****  THIS STYLING APPLIES TO ALL CHATTY ITEMS  */

	@-moz-document domain(shacknews.com) { 



	/*****  USER NAMES:
 
		There are four user name modifiers:

		(1) .oneline_user{} modifies all names in the one-line post previews, unless

		(2) .this_user{} overrides the logged in user name in the previews.

		(3) .author{} is used for all post header names, unless

		(4) .this-user{} overrides the logged in user name in the post header.

	*/

		.oneline_user {

			color       : #66CCFF 									!important;
			font-family : Arial 									!important;
			font-size   : 16px 									!important;
			font-weight : 400 									!important;
			font-variant: normal									!important;
			text-shadow : none 									!important;


			}

		.this_user {

			color       : #FF295E 									!important;
			font-family : Arial 									!important;
			font-size   : 16px 									!important;
			font-weight : 400 									!important;
			font-variant: normal									!important;
			text-shadow : none 									!important;

			}

		.author a {

			color       : #6CF 									!important;
			font-family : Arial 									!important;
			font-size   : 16px 									!important;
			font-weight : 400 									!important;
			font-variant: normal									!important;
			text-shadow : none 									!important;
			padding-left: 0px 									!important;

			}

		.this-user a {

			color       : #FF295E 									!important;
			font-family : Arial 									!important;
			font-size   : 16px 									!important;
			font-weight : 400 									!important;
			font-variant: normal									!important;
			text-shadow : none 									!important;
			padding-left: 0px 									!important;

			}

	/*****	SHACKNEWS NAME DIFFERS IN CHATTY  */

		div.fpauthor_14475 .user a {

			color: white 										!important;
			text-shadow : #51ccff 0px 0px 6px 							!important;

			}

		div.fpauthor_14475 span.user {

			color: white 										!important;
			text-shadow : #51ccff 0px 0px 6px 							!important;

			}

		div.oneline.op span.oneline_user {

			background: none									!important;

			}
	
	/*****  ROOT AND SELECTED POST  */

		div.root {

			margin: 40px 0 0px 8px 									!important;

			}

		div.fullpost {

			margin: 0px 0px 2px 0px									!important;
			padding: 8px 0px 20px 0px 								!important;
			background-color: #262420 								!important;
			border-width: 2px 									!important;
			border-color: #444 									!important;
			border-style: dotted 									!important;

			}

		span.author {

			font-family: Tahoma 									!important;
			font-size: 14px 									!important;

			}

		div.postbody {

			line-height: normal 									!important;
			margin: 5px 20px 0 23px 								!important;
			font-family: Arial 									!important;
			font-size: 16px 									!important;
			/*text-align: justify 									!important;*/

			}

		div.postdate {

			font-size: 11px 									!important;
			font-weight: normal 									!important;
			color: #A19D97 										!important;
			height: 0px 										!important;
			line-height: 0px 									!important;
			padding: 10px 5px 									!important;

			}

		div.postdate .expiration {

			display: block 										!important;
			position: relative 									!important;
			width: auto 										!important;
			height: 10px 										!important;
			bottom: 5px 										!important;
			margin: 0px 3px 0px 10px 								!important;

			}

	/*****  NWS / INFORMATIVE / POLITICAL / STUPID / OFFTOPIC  */

		div.fpmod_nws {

			border-left: 2px solid #f02e01 								!important;
			background-image: none 									!important;

			}

		div.fpmod_informative {

			border-left: 2px solid #00bff3 								!important;
			background-image: none 									!important;

			}

		div.olmod_informative a {

			background: none 									!important;

			}

		div.fpmod_political {

			border-left: 2px solid #57E964 								!important;

			}

		/*div.fpmod_stupid {

			border-left: 2px solid yellow 								!important;

			}
			
		div.fpmod_offtopic {

			border-left: 2px solid #444 								!important;

			}*/

	/*****  ONE-LINE THREAD PREVIEWS  */

		.oneline {

			font-size:16px 										!important;
			margin: 0px 0px 0px 0px 								!important;
			line-height: 20px 									!important;

			}

		.oneline a:hover {

			background: none 									!important;

			}

		.oneline a:hover span.oneline_body {

			color: white 										!important;

			}

		div.threads ul ul {

			margin: 0px 										!important;
			background-position: 0px 0px								!important;
			background-image: url(http://chattypics.com/files/commentsul_24jxxmkkun.gif)		!important;

			}

		div.threads ul ul li {

			padding: 0px 0px 0px 18px 								!important;
			background-position: 0px 0px								!important;
			background-image: url(http://chattypics.com/files/commentsbullet_3h8e0aqf6d.gif)	!important;

			}

		div.threads ul ul li.last {

			padding: 0px 0px 0px 18px 								!important;
			background-position: 0px 0px 								!important;
			background-image: url(http://chattypics.com/files/commentsbulletlast_wbfd88vhll.gif)	!important;

			}

	/*****  COLLAPSED THREAD  */

		div.threads div.collapsed {

			position: relative 									!important;
			height: 98px 										!important;
			margin: 39px -1px 25px 8px 								!important;
			padding: 1px 1px 0px 0px								!important;
			border-style: solid 									!important;
			border-width: 0px 									!important;
			border-color: yellow 									!important;
			opacity: 1 										!important;

			}

		div.threads div.collapsed div.fullpost {

			position: relative 									!important;
			height: 86px 										!important;
			width: auto 										!important;
			border-style: dotted 									!important;
			border-width: 2px 									!important;
			border-color: #444 									!important;
			margin: 0px		 								!important;
			padding: 8px 0px 0px 0px 								!important;

			}

		div.threads div.collapsed div.postbody {

			position: relative 									!important;
			width: auto 										!important;
			border-style: solid 									!important;
			border-width: 0px  									!important;
			border-color: blue 									!important;
			margin: 1px 44px 0px 1px 								!important;
			padding: 4px 0px 4px 0px 								!important;
			left: 22px 										!important;

			}

		div.collapsed div.postmeta a.showpost:link,
		div.collapsed div.postmeta a.showpost:visited,
		div.collapsed div.postmeta a.showpost:active {

			width: 100% 										!important;
			color: #E9E9DE 										!important;
			background: -moz-linear-gradient(top, #403C36, #3B3731) 				!important;
			background: -webkit-linear-gradient(top, #403C36, #3B3731) 				!important;
			font-family : Calibri 									!important;
			font-size: 18px 									!important;
			font-weight: 400 									!important;
			font-variant: small-caps 								!important;
			text-shadow : #AAAAAA 0px 0px 6px 							!important;
			border-style: dotted 									!important;
			border-width: 2px 0px 0px 0px								!important;
			border-color: #444 									!important;
			height: 29px 										!important;
			margin: 3px 0px 0px 0px									!important;
			padding: 5px 0px 0px 0px 								!important;

			}

		div.collapsed div.postmeta a.showpost:hover {

			color: white 										!important;
			text-shadow : #AAAAAA 0px 0px 6px 							!important;

			}

	/*****  TRUNCATED THREAD  */

		div.capped div.fullpost {

			margin: 0px 0px 0px 0px 								!important;
			/*border: 1px solid red 								!important;*/

			}

		div.capped div.capcontainer {

			height: 517px 										!important;
			border: 0px solid yellow 								!important;
			margin: 0px 0px 0px 0px 								!important;
			padding: 0px 0px 0px 0px 								!important;

			}

		div.capped p.capnote {

			position:relative 									!important;
			background: #262420 									!important;
			width: auto 										!important;
			height: 34px 										!important;
			border-width: 0px 2px 2px 2px 								!important;
			border-color: #444 									!important;
			border-style: dotted 									!important;
			/*border: 1px solid yellow 								!important;*/

			}

		div.capped p.capnote a:link,
		div.capped p.capnote a:visited,
		div.capped p.capnote a:active {

			color       : #E9E9DE 									!important;
			background  : -moz-linear-gradient(top, #403C36, #3B3731)				!important;
			background  : -webkit-linear-gradient(top, #403C36, #3B3731)				!important;
			font-family : Calibri 									!important;
			font-size   : 18px 									!important;
			font-weight : 400 									!important;
			font-variant: small-caps 								!important;
			text-shadow : #E9E9DE 0px 0px 6px 							!important;
			width: 100% 										!important;
			height: 27px 										!important;
			position:absolute 									!important;
			padding: 7px 0px 0px 0px 								!important;
			text-align:center 									!important;
			/*border: 1px solid red 								!important;*/

			}

		div.capped p.capnote a:hover {

			color: white 										!important;

			}

		div.capped p.capnote strong {

			color: #FF295E 										!important;
			text-shadow : #FF295E 0px 0px 6px 							!important;
			font-size: 16px 									!important;
			font-family: Arial 									!important;

			}

	/*****  REPLY BOX  */

		div.newcommentform {

