@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wiki.pbbghub.com") {

html,
body{
  background-color:#101010!important;
  color:#efefef!important;
}
body>table{
  background-color:inherit!important;
  color:inherit!important;
  width:820px!important;
}
td{
  background-color:inherit!important;
  color:inherit!important;
}
table[bgcolor="#ffffff"],
td[bgcolor="#ffffff"]{
  background-color:#444!important;
}
font[style*="rgb(0, 0, 0)"]{
  color:#efefef!important;
}
td.grey{
  background-color:#101010!important;
  color:#efefef!important;
  height:auto!important;
  border:1px solid #9D9DA1!important;
  width:33%!important;
  display:block;
  float:right;
  -moz-border-radius: 5px;
  margin:0px 10px 5px 0px!important;
}
td.grey[height="100"]{
  margin:10px 10px 0px 0px!important;
}
input.forms_txt,
textarea{
  background-color:#303030!important;
  color:#efefef!important;
  -moz-box-shadow: 0 0 5px 3px #666;
  -moz-border-radius: 5px;
  padding: 3px;
}
a,
input{
  display:inline-block;
  background-color:#101010!important;
  color:#8DAF00!important;
  -moz-border-radius: 5px;
  padding: 0px 3px;
  margin: 2px 0px;
  text-decoration:none!important;
  height:auto!important;
}
a:hover,
input:hover{
  color:#DFB137!important;
  -moz-box-shadow: 0 0 3px 1px #DFB137;
}
hr{
  background-color:transparent!important;
  -moz-box-shadow: 0 0 5px 3px #333;
  width:30%!important;
  height:1px!important;
}
img[width="800"]{
  -moz-box-shadow: 0 0 10px 8px #0039FE;
  -moz-border-radius: 5px;
  padding: 0px!important;
  margin:0px!important;
}

}
