css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.shoujoai.com/forum/) { 

/* mwForum Default Style 2.9.3 */

/* Canvas */
html {
	margin: 0 0 1px !important;
	height: 100% !important;
	background-color: #fff !important;
	padding: 0 !important;
}
body {
	margin: 0 !important;
	background-color: #fff !important;
	padding: 10px !important;
}

/* Base font (IE 5.x doesn't inherit font to table) */
body, table {
	font-size: 13px !important;
	font-family: verdana, sans-serif !important;
}

/* Link */
a {
	color: blue !important;
	text-decoration: underline !important;
}
a:visited {
	color: purple !important;
}
a:hover {
	color: red !important;
}
a img {
	border: none !important;
}

/* Generic frame */
div.frm {
	margin-top: 10px !important;

}

/* Generic header cell */
div.hcl {
	height: 18px !important;
	background-color: #ccc !important;
	padding: 2px 4px !important;
	overflow: hidden !important;
}

/* Generic content cell */
div.ccl {
	background-color: #eee !important;
	padding: 4px !important;
}
div.ccl:first-child {
	border-top: none !important;
}
div.ccl p:first-child {
	margin-top: 0 !important;
}

/* Generic button cell */
div.bcl {
	border-top: none !important;
	background-color: #fff !important;
	padding: 2px 4px 2px 1px !important;
	text-align: right !important;
}

/* Button link */
div.bcl a, a.btl {
	margin: 1px !important;
	padding: 0 4px !important;
}
img.btl {
	vertical-align: bottom !important;
}
div.bcl a:hover, a.btl:hover {
	margin: 0 !important;
	border: 1px solid silver !important;
	background-color: #fafafa !important;
}
div.bcl a:before, a.btl:before {
	content: "[" !important;
}
div.bcl a:after, a.btl:after {
	content: "]" !important;
}

td.shr a.btl {
	padding: 0 2px !important;
}

/* Nav button, page link float */
span.nav, span.pln {
	float: right !important;
	text-align: center !important;
}

/* Page button link */
span.pln a {
	margin: 0 !important;
	padding: 0 !important;
}
span.pln a:hover {
	margin: 0 !important;
	border: none !important;
	background-color: inherit !important;
}

/* Table frame */
table.tbl {
	margin-top: 10px !important;
	width: 100% !important;
	border: none !important;
	border-collapse: collapse !important;
	border-spacing: 0 !important;
	empty-cells: show !important;
}

/* Table header row */
table.tbl tr.hrw {
	background-color: #ccc !important;
	font-weight: bold !important;
}

/* Table header cell */
table.tbl th {
	border: none !important;
	padding: 2px 4px !important;
	text-align: left !important;
	white-space: nowrap !important;
}

/* Table header column cell */
table.tbl td.hco {
	width: 20% !important;
	font-weight: bold !important;
}

/* Table content row */
table.tbl tr.crw {
	background-color: #ddd !important;
}	

/* Table content cell */
table.tbl td {
	border: none !important;
	padding: 2px 4px !important;
}

/* Table cell short */
table.tbl .shr {
	width: 15% !important;
	white-space: nowrap !important;
}

/* Table cell icon */
table.tbl img.ico {
	margin: 1px 0 0 0 !important;
}

/* Top bar frame */
div.tpb {
	margin-top: 0 !important;
	text-align: center !important;
}

/* Error header cell */
div.err div.hcl {
	background-color: #888 !important;
}

/* Error message paragraph */
div.err div.ccl p {
	margin: 5px 0 0 0 !important;
	color: red !important;
}
div.err div.ccl p:first-child {
	margin: 0 !important;
}

/* Form */
form {
	margin: 0 !important;
	padding: 0 !important;
}
input, button, textarea, select {
	font-size: inherit !important;
	font-family: inherit !important;
}
textarea {
	width: 99% !important;
}

/* Poll result */
td.plv, td.plp {
	padding-left: 15px !important;
	text-align: right !important;
}

/* Poll result bar */
img.plb {
	margin-left: 10px !important;
	height: 10px !important;
	background-color: red !important;
}

/* Post header of new posts */
div.pst.new div.hcl {
	background-color: #999 !important;

}

/* Post header of target posts */
div.pst.tgt div.hcl {
	background-color: #c4c4ff !important;
}

/* Quote */
blockquote {
	margin: 0 !important;
	color: gray !important;
}
blockquote p {
	margin: 0 !important;
}

/* Rank image */
img.rnk {
	margin: 2px 0 !important;
	vertical-align: text-top !important;
}

/* Avatar image */
img.ava {
	width: 50px !important;
	height: 50px !important;
}
div.pst img.ava {
	margin-left: 5px !important;
	float: right !important;
}
form.agl img.ava {
	margin: 2px !important;
	vertical-align: middle !important;
}

/* Make post cell enclose avatar */
div.pst div.ccl:after {
	content: "." !important; 
	display: block !important; 
	visibility: hidden !important;
	height: 0 !important; 
	clear: both !important; 
}

/* Icon */
img.ico {
	margin: 2px 0 !important;
	vertical-align: text-top !important;
	width: 14px !important;
	height: 14px !important;
	cursor: pointer !important;
}

/* Smiley icon */
img.sml {
	margin-top: 1px !important;
	vertical-align: text-top !important;
	width: 14px !important;
	height: 14px !important;
}

/* Flag */
img.flg {
	margin: 2px 0 !important;
	vertical-align: text-top !important;
	width: 18px !important;
	height: 12px !important;
}

/* Header title */
span.htt {
	font-weight: bold !important;
}

/* Sticky topic title */
span.stk {
	font-weight: bold !important;
	color: red !important;
}

/* Execution message */
span.tbm {
	font-weight: bold !important;
	color: green !important;
}

/* Highlighted keyword, report/message link */
em {
	font-weight: bold !important;
	font-style: normal !important;
	color: red !important;
}

/* Monospace font */
tt {
	font-size: 90% !important;
}

/* Float right */
.flr {
	float: right !important;
}

/* Help */
div.hlp h3 {
	margin: 10px 0 0 0 !important;
	font-size: 100% !important;
	font-weight: bold !important;
}
div.hlp p {
	margin: 5px 0 0 0 !important;
}

/* Forum info copyright, mini banner */
div.icr, div.bni {
	margin-top: 10px !important;
	text-align: center !important;
}
div.icr {
	border: 1px solid silver !important;
}
div.bni img {
	margin: 5px !important;
}

/* Board jump form */
form.bjp {
	margin-top: 10px !important;
	float: right !important;
}

/* Copyright, page timer messages */
p.cpr, p.pct {
	text-align: center !important;
	font-size: 85% !important;
	color: silver !important;
}

/* Forum config */
form.cfg h3 {
	margin: 20px 0 10px 0 !important;
	border-top: 1px solid silver !important;
	padding-top: 10px !important;
	font-size: 100% !important;
	font-weight: bold !important;
}
form.cfg h3:first-child {
	margin-top: 0 !important;
	border-top: none !important;
}
form.cfg h4 {
	margin: 10px 0 0 10px !important;
	font-size: 100% !important;
	font-weight: bold !important;
}
form.cfg p {
	margin: 5px 0 0 20px !important;
	width: 90% !important;
}
form.cfg samp {
	color: green !important;
	font-family: verdana, sans-serif !important;
	font-weight: normal !important;
}
form.cfg dfn {
	color: gray !important;
	font-style: normal !important;
	font-weight: normal !important;
}
form.cfg input.ctx {
	width: 50% !important;
}
form.cfg textarea {
	width: 90% !important;
}

/* Tag buttons */
button.tbt {
	margin: 0 3px 3px 0 !important;
	width: 40px !important;
	border: 1px solid gray !important;
	background-color: #efefef !important;
}
button.tbt:hover {
	background-color: white !important;
}

/* Invisible table */
table.tiv {
	border-collapse: collapse !important;
	border: none !important;
}
table.tiv tr {
	border: none !important;
}
table.tiv td {
	border: none !important;
	padding: 0 5px 0 0 !important;
	vertical-align: top !important;
}
table.tiv .shr {
	width: 15% !important;
	white-space: nowrap !important;
}

/* Topic tags */
img.ttg {
	vertical-align: text-top !important;
}

/* Image gallery */
table.igl td {
	text-align: center !important;
	vertical-align: bottom !important;
	font-size: 85% !important;
}

/* Attachment thumbnail */
img.amt {
	border: 1px solid blue !important;
}

TABLE.std {
	background-color: #ccc !important;
}

}
