css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

body { font-family: "Droid Sans","Segoe UI",sans-serif !important; font-size: 12px!important; color: #DCDCCC !important;  margin: 0 auto !important; padding: 0; background: #3F3F3F !important; z-index:-100}
form[name="delform"]{ width:90% !important; max-width: 1280px !important; margin: 0 auto !important; background: #333333; padding: 0px 5px 5px 5px;/* border-left: 1px solid #222; border-right:1px solid #222;*/ float:left !important; }
form[action=""]{padding:0px !important;}

#recent-images li{ background: #1c1c1c !important; }
#header { width: 100% !important; }
#navtop,#navtopr { font-size: 12px !important;/* border-bottom: 1px solid #1c1c1c*/; top: 0; position: fixed; padding: 0; padding: 5px !important; background: #3F4040 !important; text-align: center !important; z-index: 1009;display:inline; }

div.logo { position: fixed !important; float: right !important; text-align:right !important;  z-index:-99; right:5px;}
div.logo font span { visibility: visible; font-size: 20px !important; color: #EFEFEF !important; font-weight:Normal !important; font-family: "Droid Sans","Segoe UI",sans-serif !important;}
div.logo>font[size="1"]{color:#ECBCBC !important;}
div.logo img{display:none;}

SPAN[style="left: 5px; position: absolute;"] {display:none !important;}
center font[color="red"]{display:none !important;}
BODY TABLE[width="100%"]{ padding-top: 0px !important; padding-left: 10px !important; padding-right: 10px !important; margin-bottom: -1px;}
BODY TABLE[width="100%"] TH { padding: 8px !important;}

.postarea { background: #333333; padding-top:0px !important; width:90%; padding: 25px 5px 5px 5px;}
.postarea form div {border: none !important;}
.postarea input{ }
.postarea form {/*border-bottom: 1px solid #212121;*/ padding-bottom: 18px!important;}

.postblock { border: 0 !important; background: #1c1c1c !important; color: #E3CEAB !important; }
a { color: #DCDCCC !important; border: 0 !important;font-style:italic !important; text-decoration:none !important;}
#navtop>a{font-style:normal !important;}
.pages td a{font-style:normal !important;}
a:hover { color: #DCA3A3 !important; background: #333333 !important; }

th { background: #222 !important; padding: 10px !important; display:none;}
td.reply{ width: 900px !important; border: none !important; background-color:#3F4040 !important; clear: both; }
.pages { background: #464646 !important; border: 0 !important; text-align: center !important; float:right !important }
.pages td, .pages td b {font-size: 12px !important; color: #333333 !important; }

.postername, .abbr { color: #DCA3A3 !important; }
.commentpostername { color: #DCA3A3 !important; }
.postertrip { color: #7F9F7F !important;}
.filetitle { color: #8F8F8F !important; }
a img { clear: both !important; margin: 0 10px 10px 10px !important; /*border: 1px solid #AAA !important;*/ position:relative; z-index:0; }
.deletebuttons input:not([type="checkbox"]) { width: 75px !important; display: inline !important; }
.deletebuttons input[type="checkbox"] { position: relative; top: 3px; }
.quotejs { visibility: visible;   }

.replytitle{ color: #82A282 !important; }

/*body form input {display: none !important; }*/

body form { font-size: 12px !important; }
td {font-size: 12px !important;} 

.omittedposts {color: #CFBFAF !important; visibility: visible !important; margin-left: 10px;}
.filesize {  padding-left: 2px; display: inline-block; padding-top: 3px; padding-bottom: 6px; }

/* top right bottom left */
body form blockquote { background: #333333; padding: 10px; margin: 8px 2px 8px 20px;}
td blockquote { background: none !important; margin: 0 !important; padding: 5px !important;  }
form td{padding: 2px 6px !important}



 #hd, #ft, hr, td small, .doubledash, #footer, .rules, center font h3, center font small, center small font { display: none !important; }


/*4chan ext***************************/
img[class*="4chan_ext"]{
display: inline;
margin: 0 !important; 
border: 0 !important;
}

div[id*="twi_table"]{


}

.reply{ 
background: #1C1C1C !important;

/*border: 1px solid #444 !important;*/
color: #DCDCCC !important;
font-size: 12px !important;
}

.postername , .postertrip, .filetitle, .commentpostername{
font-size: 12px!important;
}

.reply[id*="ext"]{ 
padding: 1px !important;
}

div[id*="twi_table"] .postblock{
font-size: 12px !important;
padding: 3px !important;
}

form[id*="4chan_qr_form"]{
width: auto !important;
padding: 5px;

}

span[class*="4chan_win_header_buttons"] img{
display: inline;
padding: 1px;
}
/**************************************/

/*Center Images When you look at them*/
img:not(url^="chrome-extension://pinelipedelckihohgdlpcclgocodhjj/")){ display: block; margin: 0 auto; position:absolute; z-index:-99; }

/*Remove Advertising*/
img[src*="http://static.4chan.org/support"]{
display:none !important;
}
/*4chan plus stuff*/
#popup {
position: absolute; border: none !important; border-radius: 0px !important; -webkit-box-shadow: none !important; z-index: 995; display: none; padding: 0px; background-color:#333333 !important;
color:#DCDCCC !important; max-width:60% !important;}
#quickrep {
position: fixed; border: 0px solid white !important; border-radius: 0px !important; -webkit-box-shadow:none !important; right:0 !important; bottom:0 !important; top: auto !important; left: auto !important; z-index: 992; display: none; background: #333333 !important;
}
#navbar {
position: fixed; bottom: 0px; height: 20px; padding: 5px 5px 3px; border:none !important; border-radius:0px ; -webkit-box-shadow: none !important; font-size: 15px; z-index: 997; background:#313C36 !important; color: #709080 !important;
}
#navbar:not(:hover){opacity: .2 !important;}
.navbar2{left:40% !important;}
/*filter.next stuff*/
.sage:not(:hover) blockquote,
.sage:not(:hover) img {
  display: none !important;
}
#filter:not(:hover), #qr>form{background-color:#3F4040 !important; z-index:-90; border:none !important;}
#filter:hover,.dialog,td.reply>tr>td,#backwash_tooltip{background-color:#3F4040 !important; z-index:1000; border:1px solid #333 !important;}
/*Rollin' in the .3 update.*/
img#recaptcha_reload { height: 0px !important; padding-top: 18px !important; width: 25px !important; background-image: url(data:image/png;base64,) !important; margin: 0 !important; border: none !important; }

img#recaptcha_switch_audio { height: 0px !important; padding-top: 15px !important; width: 25px !important; background-image: url(data:image/png;base64,) !important; margin: 0 !important; border: none !important; }

img#recaptcha_whatsthis { height: 0px !important; padding-top: 16px !important; width: 25px !important; background-image: url(data:image/png;base64,) !important; margin: 0 !important; border: none !important; }

#recaptcha_logo, #recaptcha_tagline { display: none; }

.unkfunc{color:#9ECE9E !important; font-weight:bold !important;}
.unkfunc>a{font-weight:normal !important;}

.fc-panel{display:inline-block !important;}

input, input[type="submit"], input[type="button"], select, button, textarea {-moz-appearance: none; border: 1px #aaa solid !important; background: #3F3F3F !important; color: #DCDCCC !important;}

span.spoiler{color:#41363C!important;
background-color:#41363C!important;
}
span.spoiler:hover{color:#ECBCBC!important;}

/*Flashblock stuff*/
div[style*="flashblock"]{
height:20px !important;
width:20px!important
}

.recaptchatable *{
color: #DCDCCC !important
}

.inputtext{
font-family: "Droid Sans","Segoe UI",sans-serif !important;
}
}
@-moz-document domain("4chan.org") {.postarea{padding-top:25px !important}}
