css:
@-moz-document url-domain("yacapaca.com") {
div.todo-header
{
    /*[[moz]]*/border-radius: .5em;
    background-color: #247;
    color: #FFF;
    border: 2px solid #469;
}

div#baner.baner { display: none; }

td.percent { font-weight: bold; }

li.pages, li.left, li.right
{
    font-color: #F9D816;
    font-size: 12px;
    font-weight: bold;
}

div.st-header-new { background-color: #000; }

div.todo-line
{
    border: none;
    /*[[moz]]*/border-radius: .5em;
}

div.npaginator ul
{
    border: 2px solid #898;
    /*[[moz]]*/border-radius: .5em;
}
}
