@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://userstyles.org/"), 
               url-prefix("http://userstyles.org/user/"), 
               url-prefix("http://userstyles.org/users/"), 
               url-prefix("http://userstyles.org/style/"), 
               url-prefix("http://userstyles.org/styles/"), 
               url-prefix("http://userstyles.org/styles;"), 
               url-prefix("http://userstyles.org/stylish"),
               url-prefix("http://userstyles.org/livetitle") , url("https://userstyles.org/"), url-prefix("https://userstyles.org/user/"), url-prefix("https://userstyles.org/users/"), url-prefix("https://userstyles.org/style/"), url-prefix("https://userstyles.org/styles/"), url-prefix("https://userstyles.org/styles;"), url-prefix("https://userstyles.org/stylish"), url-prefix("https://userstyles.org/livetitle") {

html { background: #283C51 !important; } 

body {
  color: #333 !important;
  background: #fff url(http://ll50cc.googlepages.com/page-t.jpg) 0 0 repeat-x !important;
  border: 5px solid #fff !important;
  border-top: none !important;
  border-bottom: 5px solid #fff !important;
  margin: 0 7% !important;
  padding-bottom: 8% !important;
  font-family: Trebuchet MS !important;
}

#logo a { color: #283C51  !important; font-family: Trebuchet MS !important; }

pre { border: 1px solid #afac9a !important; }

#header-bar {
  background: #EF931A !important;
  border: 7px solid #283C51 !important;
  border-top: none !important;
}

#navigation a, #navigation a:visited, #navigation .current-page {
  border: 1px solid #26466D !important;
  border-bottom: none !important;
  background: #283C51 !important;
  color: #E0E0E0 !important;
  text-decoration: none !important;
  -moz-border-radius: 15px 15px 0 0 !important;
  font-family: Trebuchet MS !important;
  cursor: pointer !important;
}

#navigation a:hover, #navigation span:hover {
  color: #fff !important;
}

a:link, #sidebar h2 a { color: #26457d !important; }

a:hover, a:hover { color: #6395C6 !important; }

a:visited { color: #6395C6 !important; }

li a:hover { color: #6395C6 !important; }

#account-link a { 
font-family: Trebuchet MS !important;
font-weight: bold !important;
color: #000000 !important;
}

input#search-terms {
  background-color: #fff !important;
  border: 1px solid #6382b1 !important;
  font-family: Trebuchet MS !important;
  font-weight: bold !important;
}

#search input[type="submit"] {
  border: 1px solid #26466D !important;
  background-color: #26466D !important;
  color: #3D3D3D !important;
}

.simple-link-ads { display: none !important; }

li a { line-height: 1.3 !important; }

