css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {


#dashboard_drafts {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_index {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_post_queue {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_submissions {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_followers {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_tumblelog_settings {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_settings {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#dashboard_edit_post {background: url('http://i45.tinypic.com/2qwncpd.jpg') center right #000000 repeat fixed !important;}

#posts .notification {border:none !important} 
#posts .notification {background-color: #2c2c2c !important;}
#posts .notification.single_notification {border:none;}
#posts .notification.first_notification {border:none;}


#posts .notification.alt {border:none; background-color: #303030 !important;}
#posts .notification.last_notification {border:none;}
#posts .notification .hide_overflow {border:none;}

#posts {border:none;}

#posts .notification .nipple {

border-bottom: 6px solid transparent;

border-right: 6px solid #2c2c2c;

border-top: 6px solid transparent;

}

#posts .notification .nipple.border {border:none;}
#posts .notification .nipple.border.alt {border:none;}
#posts .notification.alt .nipple {border-right: 6px solid #303030;}

#pagination a {background-color: #323232;}

#footer {color: #8b8b8b;}
#footer a {color: #8b8b8b;}







ol#posts li blockquote,

body.mceContentBody blockquote {

margin-left: 0 !important;

margin-right: 0 !important;

padding-left: 10px !important;

border-width: 4px !important;

border-color: #000000 !important;

}

#posts .post .post_avatar.lighter_blue {background-color: #2c2c2c; color:#8f8f8f;}
#posts .post .post_avatar.flat {border:none;}

div.l-header .selected .selection_nipple {border-bottom: 12px solid rgba(34,34,34,0.8);}

.dashboard_options_form {background-color: #2c2c2c !important;}
.dashboard_options_form {border:none !important;}
.dashboard_options_form {color: #878787 !important;}

.radar {display:none;}
#radar {display:none;}

#posts .notification a:not(.avatar_frame) {border-bottom: none;}

.dashboard_options_form .dashboard_options_form_disclaimer {color:#5c5c5c;}
.dashboard_options_form .dashboard_options_form_disclaimer a {color:#5c5c5c;}

#posts .post .post_avatar.lighter_blue #publish_on_time {color:#5c5c5c;}

#right_column .controls_section .recessed {background-color: #313131;}
#right_column .controls_section .recessed a {background-color: #313131;}#right_column .controls_section .recessed {border:none !important;}

#right_column .controls_section .with_subtitle small {color: #545454;}

#posts .notification .notification_type_icon {background: url("http://cl.ly/1m212a1a1q2c472P0K3U/dashboard_master_sprite_grey.png") no-repeat scroll 0 0 transparent;}

#dashboard_controls a, #dashboard_controls .control {color: #8a8a8a;}



ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote {

border-color: #000000 !important;

}

ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {

border-color: #000000 !important;

}

.left_column {padding: 10px 0 5px 30px;}
.right_column {margin: 0 0 0 0;}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background-color: rgba(34,34,34,0.8);
  }



h1 {
color: #333333;
text-shadow: 0 0 3px #000000;
}

.no_posts_found, h1.dashboard_header {
color: #000000;
}

img#content_top, img#content_bottom {
display: none;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
    background: none;
  }

#right_column .dashboard_nav_item {
    background: none repeat-x;
    background-attachment: initial;
    background-position-x: 50%;
    background-position-y: 100%;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
}

#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: #333333;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: rgba(0,0,0,0.0);
}

#nav .nav_item.active {
    background: rgba(00,0,0,0.5) !important;
  }

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
    background: rgba(00,0,0,0.5) !important;
  }

ol#posts li.notification.alt {
    background-color: rgba(0,0,0,0.5);
}

ol#posts li.notification.first_notification {
    background-color: rgba(0,0,0,0.5);
}

ol#posts li.notification {
    background-color: rgba(0,0,0,0.5);
    border-bottom: 1px solid #000000;
}

ol#posts li.notification.last_notification {
    border-bottom: 1px solid #000000;
}

ol#posts li.post .permalink {
    background: rgba(00,0,0,0.5);
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.8);
border: solid #000000;
}

ol#posts li.post .post_avatar{
border-bottom: 0px solid #000000;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #000000;
border-bottom: 1px solid #000000;
}

#right_column a#dashboard_switch_blog_menu_current:hover {
background: none;
}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
background: none;
}

form.dashboard_options_form {
background-color: rgba(0,0,0,0.0);
border-bottom: 1px solid #000000;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #000000;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}

form.dashboard_options_form .option_container {
border-top: 1px solid #000000;
}

.no_posts_found, h1.dashboard_header {
color: #333333;
}

}
