css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {

body{ background-image:none !important; background-color:#00CC99 !important; font-family:'MS Sans Serif' !important;}

.postblock{background:inherit !important; border-style:solid !important;  }
.inputtext, textarea, #com_submit {font-family: 'MS Sans Serif' !important;}

.logo, .rules {display:none !important;}

.reply, .replyhl  {background:inherit !important; border-style:solid !important; border-width:1px !important;  border-color:black !important;}
.replyhl {border-style:dashed !important;}
#com_submit{-moz-appearance:none !important;}
.unkfunc {color:#006600 !important;}
input[type="checkbox"] {-moz-appearance:none !important;}
html body div.postarea form table tbody tr td input
{-moz-appearance:none !important; font-family: 'MS Sans Serif' !important;}
html body table tbody tr th {background-color:inherit !important;}
hr {display:none;}
}
