css:
/*■ Main css ■*/
@-moz-document regexp("https?://(|www\\.)google\\..*q\\=.*") {

/*■ Disables Preview ■*/
div#vsrs, .vsi, div#vsia, div#vsic, div#vsm, div#vspb, div#vspc, div#vspci, .vspib, .vspi {display: none !important;}

/*■ Changes font ■*/
* {
font-family: "Segoe UI", Tahoma !important;
font-size: 10pt !important;
}

/*■ Removes ads ■*/
#pmocntr2, #prm, #taw, #bottomads, .gsad2 { display: none !important; }


/*■ Increases results width ■*/
div#cnt { min-width:99%; }
#cnt #res .g .s, .mw, .big .mw { max-width:99%;}
#cnt #center_col, #cnt #foot {
margin-left: 0px !important;
margin-right:0px !important;
max-width:99%;
width: 99%;
padding-top: 5px;
}

/*■ Removes right side column ■*/
#rhs { display:none !important; }


/*■ Moves search tools to the left ■*/
div#hdtb_msb > .hdtb_mitem:first-child.hdtb_msel { margin-left: 15px !important; }
div#hdtb_msb > .hdtb_mitem:first-child { margin-left: 6px !important; }
div#hdtb-mn-gp { width:17px; }


/*■ Makes search bar smaller ■*/
#gbx1, #gbx2, #gb, #gbx1.gbem, #gbx2.gbem, #gb.gbem, #gbx1.gbes, #gbx2.gbes, #gb.gbes, .gbem#gbx1, .gbem#gbx2, .gbem#gbqlw, .gbemi#gb #gbx1, .gbemi#gb #gbx2, .gbemi#gb #gbqlw {height: 43px !important;}
#gbqfw, #gbq3 {margin-top: -13px !important; margin-left: -60px !important;}
.gbqff { padding-left: 40px !important; }
#gb #gbu { top: 16px !important; }
#gbql, #gbql.gbem, #gbq1.gbes, #gbqld { position:absolute; top: 5px !important; left:-10px !important; }
#prs { margin-top: 14px !important; margin-left: 17px !important; }
#rcnt { margin-top: 20px !important; }
#cnt { padding-top: 1px !important; margin-top: 20px !important; }
.sfbgg { height: 44px !important; }


/*■ Makes search options always visible ■*/
#hdtb_tls { display: none !important; } /*■ removes "search options" button ■*/
#hdtbMenus.hdtb-td-o, #hdtbMenus.hdtb-td-c, .hdtb-td-h {
display: block;
overflow: visible;
border-bottom: 0px solid #EBEBEB;
top: 40px;
height: 22px;
padding: 0;
}
.hdtb-mn-cont { position: absolute; top: 15px; }


/*■ Removes the redundant bar ■*/
#hdtb_msb { display:none !important; }
#hdtbSum { float:right; width:220px; height:58px; top:10px; }
#hdtbMenus.hdtb-td-o, #hdtbMenus.hdtb-td-c, .hdtb-td-h { top: 0px; }


/*■ Lowers result stats below the search options ■*/
.ab_tnav_wrp { margin-left: 26px !important; }
#ires { padding-top: 24px; }
#resultStats, #spn_nb {
border-bottom: 1px solid #EBEBEB;
padding-left: 7px;
padding-top: 33px;
padding-bottom: 0;
padding-right: 8px;
width:100%;
}


/*■ Removes the result stats ■*/
/* #resultStats, #spn_nb { display:none !important; } */


} /*■ End of main css ■*/



/*■ Autocomplete off css ■*/
@-moz-document regexp("https?://(|www\\.)google\\..*(q\\=.*(as_qdr\\=all|complete\\=0)|(as_qdr\\=all|complete\\=0).*q\\=).*") {

/*■ Makes search bar smaller ■*/
#cnt {  padding-top: 1px;  margin-top: -12px !important; }
.hdtb-mn-cont { position: absolute; top: -8px; }
#resultStats, #spn_nb { padding-top: 10px; }
#ires { margin-top: -23px; }

} /*■ End of autocomplete off css ■*/



/*■ Image search css ■*/
@-moz-document regexp("https?://(|www\\.)google\\..*(q\\=.*tbm\\=isch|tbm\\=isch.*q\\=).*") {

/*■ Makes search bar smaller ■*/
#gbx1, #gbx2, #gb, #gbx1.gbem, #gbx2.gbem, #gb.gbem, #gbx1.gbes, #gbx2.gbes, #gb.gbes {height: 43px !important;}
.gbem#gbx1, .gbem#gbx2, .gbem#gbqlw, .gbemi#gb #gbx1, .gbemi#gb #gbx2, .gbemi#gb #gbqlw {height: 43px !important;}
#gbqfw, #gbq3 {margin-top: -13px !important; margin-left: -60px !important;}
.gbqff { padding-left: 40px !important; }
#gb #gbu { top: 16px !important; }
#gbql, #gbql.gbem, #gbq1.gbes, #gbqld { position:absolute; top: 5px; left:-10px; }
#prs { margin-top: 2px !important; margin-left: 17px; }
#rcnt { margin-top: 15px; }
#cnt { margin-top: 15px !important; }

/*■ Moves the search options down ■*/
.hdtb-mn-cont { position: absolute; top: 20px; }

/*■ Safesearch and options button ■*/
#hdtbSum { top:15px; border-bottom: 0px solid #FFFFFF; }

} /*■ End of image search css ■*/
