@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {
body {background: #EAEBEF !important; color: #101411 !important;}

a {color: #50535B !important; text-decoration: none;}
a:hover {color: #CC2C1E !important; text-decoration: none !important;}

body, textarea, input {font: 12px Tahoma !important;}

.box-bar {background: #D3D4D8 !important; border: 1px solid #D3D4D8 !important; text-shadow: 1px 1px #fff; color: #50535B !important;}

.textbox {padding: 5px !important;}
.textbox textarea {direction: rtl !important;}

.ebody .title .text {font-size: 13px !important; line-height: 160% !important; direction: rtl !important; text-align: left;}

.info, .likes {font-size: 11px !important;}
.likes {color: #aaa !important;}
.likes a {text-shadow: 1px 1px #ddd;}

.entry .comment {line-height: 160% !important; direction: rtl !important; text-align: left;}

.owner {background-color: #eee !important; -webkit-border-radius: 5px !important; -moz-border-radius: 5px !important; -webkit-box-shadow:1px 1px 1px #ccc; -moz-box-shadow:1px 1px 1px #ccc; padding: 5px; text-shadow: 1px 1px #fff;}


