css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("forum.football365.com")
{

/* 09:04 11/03/04 Fixed issue with mod control panel disappearing */
/* 09:43 11/03/04 Fiddled with line heights a bit in quotes/posts */
/* 09:57 11/03/04 Finally did something with the posting form */

#wrapcentre {margin: 0 25px 0 ! important;}	

.gen {
	margin: 1px 1px;
	font-size: 1.1em ! important;
}
th { background-image: none ! important; }
td {padding: 0px ! important;}

p.gensmall{display:inline ! important;}
/* #pageheader {display:none ! important;margin:0 ! important;} stops the mod panel link going*/
#datebar {display:none}

.postauthor {	font-weight: bold ! important;
font-size: 1.1em ! important;}

.breadcrumbs { font-size: 1.1em ! important;
font-family:  Helvetica, Arial, sans-serif ! important;
}

td.gensmall {font-size: 1.1em ! important;
font-family:  Helvetica, Arial, sans-serif ! important;
}

input {
	color: #333333;
	font-family:  Helvetica, Arial, sans-serif ! important;
	font-size: 1.1em ! important;
	line-height: 1.2em ! important;
	font-weight: normal;
	padding: 1px;
	background-color: #FAFAFA;
}

.topicauthor { font-size: 1.0em ! important;}
.topicdetails { font-size: 1.0em ! important;}
.topictitle { font-family:  Helvetica, Arial, sans-serif ! important;
	font-size: 1.1em ! important;
	font-weight: bold! important;
	color: #005784 !important;
	text-decoration: none !important; }
textarea {
	background-color: #FAFAFA;
	color: #333333;
	font-family:  Helvetica, Arial, sans-serif ! important;
	font-size: 1.1em ! important; 
	line-height: 1.2em ! important;
	font-weight: normal;
	padding: 2px;
}

select {
	color: #333333;
	background-color: #FAFAFA;
	font-family:  Helvetica, Arial, sans-serif ! important;
	font-size: 1.1em ! important;
	font-weight: normal;
	padding: 1px;
}
h1 { font-family:  Helvetica, Arial, sans-serif ! important; }
.row1 { background-color: #e3e3e3 ! important; font-family:  Helvetica, Arial, sans-serif ! important; }
.row2 { background-color: #F7F7F7 ! important; font-family:  Helvetica, Arial, sans-serif ! important; }
.row3 { background-color: #e3e3e3 ! important; font-family:  Helvetica, Arial, sans-serif ! important; }

a.forumlink {
	color: #069;
	font-weight: bold;
	font-family:  Helvetica, Arial, sans-serif ! important;
	font-size: 1.2em;
}

.pagination {
	padding: 0px ! important;
	color: black;
	font-size: 1.1em ! important;
	font-weight: normal ! important;
}
.postbody {
	font-size: 1.1em ! important;
	line-height: 1.2em ! important;
	font-family:  Helvetica, Arial, sans-serif ! important;
	}
.postdetails {
        word-wrap: normal ! important;
        }
.quotetitle, .attachtitle {
	margin: 10px 5px 0 5px;
	padding: 2px ! important;
	border-width: 1px 1px 0 1px;
	border-style: solid;
	border-color: #A9B8C2;
	color: #333333;
	background-color: #A9B8C2;
	font-size: 0.85em ! important;
	font-weight: bold;
}

a:hover{
	color: #D46400 !important;
	text-decoration: underline !important;
}
.copyright {
	color: #444;
	font-weight: normal;
	font-family:  Helvetica, Arial, sans-serif ! important;
}

.titles {
	font-family:  Helvetica, Arial, sans-serif ! important;
	font-weight: bold;
	font-size: 0.85em ! important;
	text-decoration: none;
}
.quotetitle .quotetitle {
	font-size: 1em ! important;
}
.quotecontent, .attachcontent {
	margin: 0 5px 10px 5px;
	padding: 2px ! important;
	border-color: #A9B8C2;
	border-width: 0 1px 1px 1px;
	border-style: solid;
	font-weight: normal;
	font-size: 1em ! important;
	line-height: 1.1em ! important;
	font-family:  Helvetica, Arial, sans-serif ! important;
	background-color: #f3f3f3 ! important;
	color: #4B5C77;
}
td {
	padding: 0px ! important;
}
td.profile {
	padding: 0px ! important;
}
	/* specifies width of web search results column */
	td.j {
		width: 75% !important;
	}
}
