css:
#main-window[stylish-url="about:support"] #page-proxy-favicon, .console-row[type="message"], .statusIcon[status="success"], notification[type="info"] .messageImage{
	  list-style-image: url("data:image/png;base64,") !important;}

#main-window[stylish-url="about:neterror"] #page-proxy-favicon, #main-window[stylish-url="about:certerror"] #page-proxy-favicon, .tab-icon-image[src*="warning-16.png"], 
#page-proxy-favicon[src*="warning-16.png"], .bookmark-item[src*="warning-16.png"], .tabbrowser-tab[label="/*[[Idioma]]*/"] .tab-icon-image {
	padding-left:16px !important;
	background: url("chrome://mozapps/skin/extensions/alerticon-warning.png") center  no-repeat !important;}

.bookmark-item[label="/*[[Idioma]]*/"], notification[type="warning"] .messageImage, .console-row[type="warning"] {
	list-style-image: url("chrome://mozapps/skin/extensions/alerticon-warning.png") !important;}


.console-row[type="error"], .console-row[type="exception"] {
	list-style-image: url("chrome://mozapps/skin/extensions/alerticon-error.png") !important;}
