css:
@-moz-document 	domain("xkcd.com"){
body{
	background-color: #96A8C8;
	font-size: 0px;
	text-align:center;
}

a, table, h1, h2, h3, h4, #topContainer, .line, #comicLinks, #licenseText, .dialog .hd .c, .dialog .ft .c {display : none;}

img {border: 2px solid #6E7B91;}

#container{
	width: 780px;
	margin-left: auto;
	margin-right: auto;
	padding-bottom: 0px;	
	font-size:0px;
}

#middleContent ul{
	margin: auto;
	padding: 32px;
	list-style-type: none;
	width: auto;
}
#middleContent li{float:left;}

#middleContent ul li a{
	display: block;
	text-align:center;
	margin: 5px 15px 5px 15px;
	width: 70px;
	border: 1px solid black;
	background-color: #6E7B91;	
	color: #FFF;
	text-decoration: none;
	font-size: 16px;
	font-variant: small-caps;	
	font-family: Lucida, sans-serif;
	font-weight:600;
}

.dialog .bd .c .s {
	margin:10px 0px 10px 0px;
	background:#96A8C8;
}
}
