css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(mobile01.com) {
div[class="widget panel-group"],
div[class="widget fast-ad"],
div[class="ad-a"],
div[class="ad-a1"],
div[class="ad-a2"],
div[class="ad-b"],
div[class="ad-c"],
div[class="ad-d"],
div[class="text-ad"],
div[class="double_ad_l"],
div[class="widget article"],
div[class="widget"],
div[class="catbar-ad"],
div[class="double_ad_r"]{
display: none !important;
}

#content {
	width:95% !important;
}

#section .main {
	width:95% !important;
}

#index-category .category {
	width:96% !important;
}

#index-category .item {
	width:49% !important;
}

.subject-text {
font-size:15px;
}

/*Enlarge font size of subject*/
#section .main table .subject-text a.topic_gen {
font-size:18px !important;
}

#index-category .category a.group h3 {
	font-size:15px;
}

#index-category .category p {
	font-size:12px;
}

.single-post-author-img {
top:-12px;
	width:75px;
	height:75px;
}
.single-post-author-img div {
	width:75px;
	height:75px;
}

#headline {
margin-left:100px;
}

/* fix youtube embedded video on iframe rendering */
/* 360p = 640x360 px , control bar ~= 30 px*/
iframe[ width="100%"][height="480"]{
width:640px !important;
height:390px !important;
}

}
