css:
@namespace url(http://www.w3.org/1999/xhtml);

/* TITLE: Flickr Tweaks HD: No Ads, Wider Search Results - http://userstyles.org/styles/44840 */

/* AUTHOR: Davey Jacobson - http://userstyles.org/users/17031 */

@-moz-document domain("flickr.com") {
#ad-block, #SearchHouseAd {display:none!important;}
}
@-moz-document url-prefix("http://www.flickr.com/search/") {
#gettysearch, #pivot_places {display:none!important;}

.zeus {overflow-x:hidden!important; margin-top:-2px!important;}

.zeus #TopBar {position:absolute; left:10px!important; top:10px!important; margin:0 -10px 0 10px!important;}
.zeus #TopBar .Header td.Status {position:absolute; right:-92%!important; top:-4px;}
.zeus #SearchForm {position:absolute; top:7px!important; left:250px!important; height:inherit!important;}
.zeus #SearchForm #SearchTable {vertical-align:top!important; margin:-5px 0 90px 0!important;}

.zeus #PivotsContainer {width:100%!important; display:inline-block!important; margin-top:0!important; position:absolute!important; top:-110px!important; right:-15px; border-top:1px dotted #dcdcdc!important; padding:33px 15px 0 15px!important;}
.zeus #PivotsContainer > div:not(#SearchHouseAd):not(#gettysearch):not(#pivot_places) {display:inline-block!important; padding:0!important; margin:0!important; line-height:14px!important; background: url("http://l.yimg.com/g/images/gradient_explore_search_box.png") repeat-x 0 100% scroll!important;}
.zeus #pivot_group {top:-21px!important;}
.zeus .pivot_grouping > div.pivot_more_link {margin:0!important; padding:0!important;}
.zeus #pivot_photographers {top:-21px!important; left:20px!important;}
.zeus #pivot_tagclusters {right:-40px!important; top:-21px!important;}
.zeus #PivotsContainer > div > div.pivot_item {width:285px!important; margin:10px!important; padding:0!important;}
.zeus #PivotsContainer > div > div.pivot_item > div.pivot_details > p.pivot_title {width:105%!important;}

.zeus #BodyContainer #ResultsContainer #MainContent, .zeus #BodyContainer #ResultsContainer #just_results {margin-right:0!important;}
}
