css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

Body{font-size:12px !important; background-image:none !important }

a, a:visited{} a:hover{}

div.footer_links, div.post_info{color:#777777 !important}
div.footer_links a, div.post_info a{color:#777777 !important}

div#container, div#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#222222 !important}
#dashboard_index, div#auto_pagination_loader {background-color:#333333 !important}

body.with_auto_paginate#dashboard_likes, body.with_auto_paginate#main_tag, body#dashboard_followers, body#dashboard_submissions, body#dashboard_edit_post, body#dashboard_inbox {background-color:#333333 !important}

}
