@-moz-document domain('psto.net') {

body {
    background-color: #201E20;
    color: gray;
}

a {
    color: #CC8000;
}

a:link {text-decoration: none}

a:hover {
    text-decoration: underline;
    color: #efefef;
}

input[type="text"], input[type="password"], textarea {
    border-color: #302E30;
    color: #666666;
    background-color: #201E20;
}

input[type="text"]:focus, input[type="password"]:focus, textarea:focus {
    border-color: #302E30;
    color: #eaeaea;
}

#hinfo {
    background-color: #302E30;
}

#hinfo > #info {
    background-color: #302E30;
}

#bots {
    color: #cdcdcd;
}

#sub-hinfo {
    background-color: #302E30;
}

#search {
    position: relative;
    background-color: #201E20;
    border-color: #50505a;
}

#search #text-input.empty {
    color: #60606c;
}

#search #mode {
    color: #60606c;
}

#search #modes {
    background-color: #201E20;
    border-left: solid 1px #50505a;
    border-right: solid 1px #50505a;
    border-bottom: solid 1px #50505a;
    color: #eaeaea;
}

#search #modes > li {
    color: #eaeaea;
}

#search #modes > li:hover {
    background-color: #3d3d42;
}

#tabs > a {
    color: #80808c;
    border-bottom: solid 3px #3d3d42;
}

#tabs > a:hover {
    border-bottom: solid 3px #38383c;
}

#tabs > a.active {
    border-bottom: solid 3px #80808c;
    text-shadow: #2c2c2f 1px 1px 1px;
    filter: Shadow(Color=#CC8000, Direction=45, Strength=1);
}

#footer > #menu {
    background-color: #201E20;
    color: #cdcdcd;
}

#footer > #menu > a {
    color: #cdcdcd;
}

#footer > #copyright {
    background-color: #201E20;
    color: #cdcdcd;
    border-top: solid 5px #201E20;
}

#footer > #copyright > a {
    color: #E0E0E0;
}

.post .tags,
.post .tags a {
    color: #80808c;
}

.post .info {
    color: #80808c;
}

.comments {
    border-top: solid 1px #3d3d42;
}

#subs-h, #readers-h {
    border-bottom: dashed 1px #60606c;
}

.post-form-div #post-input {
    border-color: #302E30;
}

.post-form-div #post-input.empty {
    color: #201E20;
}

.post-form-div #post-input:focus {
    border-color: #a0a0a0;
    color: #eaeaea;
}

.post-form-div #tags-input {
    border-color: #302E30;
    background-color: #201E20;
}

.post-form-div #tags-input:focus {
    border-color: #a0a0a0;
    color: #eaeaea;
}

.dialog {
    background-color: #44444f;
    -moz-box-shadow: 0 0 20px #60606c;
    -webkit-box-shadow: 0 0 20px #60606c;
    box-shadow: 0 0 20px #60606c;
    /*filter:progid:DXImageTransform.Microsoft.Blur(PixelRadius='100', MakeShadow='true', ShadowOpacity='0.40');*/
}

.dialog .buttons {
    background-color: #3d3d42;
}

#login-form-div .error {
    color: #800000;
}

.row .input .buttons {
    color: #d0d0d0;
}

input.error,
textarea.error {
    border-color: #800000;
    background-color: #ffffa0;
}
}
