css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.aozora.gr.jp/cards/) {
body {
    background: #F2EBC8;
    text-shadow: 0 0 1px rgba(0,0,0,0);
    font-family: 'ヒラギノ明朝 Pro W3', 'Hiragino Mincho Pro', 'ＭＳ Ｐ明朝', serif;
    text-align: center;
}
body > * {
    background: #F2EBC8;
    width: 700px;
    margin: 0 auto;
    text-align: left;
    -webkit-box-sizing: border-box; 
    -moz-box-sizing: border-box; 
}
div, h1, h2 {
    line-height: inherit;
}
h1 { 
    font-size: 36px;
    background: rgba(0,0,0,.05);
    line-height: 2;
    text-shadow: 1px 1px 1px rgba(0,0,0,.1),-1px -1px 1px #fff;
    margin-bottom: 1em;
}
h2 {
    font-size: 20px;
    font-weight: 500;
    border: solid rgba(0,0,0,.05);
    border-width: 1px 0;
    line-height: 2;
    margin: 0 auto;
    display: block;
    padding: 0 1em;
    text-align: right;
}
h2.subtitle {
    border-bottom: dotted 1px rgba(0,0,0,.05);    
    text-align: left;
    margin-bottom: 1px;
}
h2.author,
h2.translator {
    border-top: none;
}
h1:before {
    content: '『';
}
h1:after {
    content: '』';
}
div.main_text {
    font-size: 20px;
    line-height: 34px !important;
    color: #000;
}
rt,
rp,
hr {
  display: none;
}
.bibliographical_information {
    padding: 1.5em;
    background: rgba(0,0,0,.9);
    color: #fff;
    font-family: 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Mayryo, 'ＭＳ Ｐゴシック', 'MS PGothic', sans-serif;
    -moz-border-radius: 6px;
}
.bibliographical_information a {
    background: rgba(255,255,255,.9);
    font-weight: 800;
    padding: 0 .5em;
}
.notation_notes {
    font-family: 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Mayryo, 'ＭＳ Ｐゴシック', 'MS PGothic', sans-serif;
    font-size: 16px;
}
}
