css:
/*
A gray-blue style for Not The Talk which highlights different areas 
(folders, discussions, posts, etc).
Written by blacksunday.
Version 1.0 
 */

@-moz-document domain("notthetalk.com") {

html * {
    margin: 0;
    /*padding: 0; SELECT NOT DISPLAYED CORRECTLY IN FIREFOX */
}

h2 {
}

div {
    font-size: 103%;
    line-height: 1.5;
}

body {
    font: 13px verdana, arial, helvetica, sans-serif;
    background: #668099;
}

a
{
	color: #000036
}

#gutlogo, #toplogo
{
	border: none;
}
	
#maincolumn
{
	width: 780px;
	margin-left: auto;
	margin-right: auto;
        background-color: white;
        padding: 20px;
        position: relative;
        top: -140px;
}

#header
{
	margin-top: 120px;
	margin-bottom: 16px;
        //float: right;
	
}

#contentheader
{
	margin-left: 160px;
        background-color: #c0c0c0;
	color: black;
	height: 48px;
	margin-bottom: 32px;
}

#headerText
{
	padding: 8px;
	float: left;
}

#headerText h1 a
{
	text-decoration: none;
}

#footer
{
	margin-bottom: 240px;
}

#email
{
	width: 240px;
}

.frontpagediscussion a
{
	text-decoration: none;
}

#contentheader-right
{
	background-color: black;
	color: white;
	width: 161px;
	float: right;
	padding: 4px;
	height: 40px;
        font-weight: bold;
}
#contentheader-right a
{
	text-decoration: underline;
	color: white;
}

.signupcol1
{
	width: 200px;
	vertical-align: top;
}

.instruction
{
	font-size: 9px;
	font-style: italic;
	margin-left: 4px;
}

.discussionlist
{
	margin-top: 4px;
	margin-bottom: 12px;
        background-color: WhiteSmoke;
        padding: 4px;
}

.folderList a
{
	color: #000036
}

#folders
{
	float: left;
        font-weight: bold;
        background-color: Silver;
        padding: 10px;
}

#discussions
{
	margin-left: 168px;
}

#discussionsHeading
{
	margin-top: 12px;
	margin-bottom: 12px;
}

.noleftcolumn
{
	margin-left: 161px;
	width: 436px;
	padding-left: 0px;
	padding-right: 0px;
}

.bold
{
	font-weight: bold
}

.margin12
{
	margin-bottom: 8px;
	padding-bottom: 8px;
}

.margin2
{
}

.bordered
{
	border-bottom: solid #c0c0c0 1px;
}

.headertext
{
	color: #000036
}

#rightcontent
{
	float: right;
	width: 152px;
	padding-left: 8px;
}

.nobullets
{
	list-style: none;
	margin-left: -40px
}

.post-quoted
{
	margin-left: 36px;
	margin-right: 36px;
	font-family: courier;
}

.post-italic
{
	font-style: italic;
}

.post-bold
{
	font-weight: bold;
}

.post-centre
{
	text-align: center;
}

.post-underline
{
	text-decoration: underline;
}

.post-noindentbullet
{
	padding-left: 1.5em;
}

.post-indent1
{
	padding-left: 2em;
}

.post-indent2
{
	padding-left: 4em;
}

.threadheading
{
	background-color: #c0c0c0;
	padding: 4px;
	width: 442px;
}

.top24
{
	//margin-top: 36px;
}

.posttextarea
{
	display: block; 
	vertical-align: top;
	width: 394px;
	height: 180px;
}

.postbody
{
        background-color: WhiteSmoke;
        positioning: relative;
        padding: 4px;
}

.threadlocked
{
	font-style: italic;
	color: red;
}

.rederror
{
	font-weight: bold;
	color: red;
}

.biotext
{
	display: block; 
	vertical-align: top;
	width: 400px;
	height: 180px;
}



}
