css:
@namespace
 url(http://www.w3.org/1999/xhtml);

@-moz-document
 url("krautchan.net"),
 url-prefix(http://krautchan.net),
 domain("www.krautchan.net") {

textarea#postform_comment {
 width: 400px; height: 150px; }

body {
 background: #EDDAD2 !important; color: #34345C !important;
}
* {
  font-family: "Trebuchet MS", Tahoma, Verdana, Arial, sans-serif;
  font-size: 10pt;
}
h1 {
 margin-top: 10px !important; }

h1, form, td, .postbody, .newsbody {
 color: #800517 !important; }

.site_name {
 color: #AF0A0F !important; }

.postsubject {
 font-size: 12pt !important;
 color: #C11B17 !important; }

h2 {
 margin-bottom: 10px !important; }

.menu_title {
 font-size: 14pt !important;
 color: #000000 !important;
 background: #D9AF9E !important;
 margin-bottom: 0px !important; }

.postbody {
background: #EDDAD2 !important; }

.file_thread, h2, span.spoiler:hover {
 background: #EDDAD2 !important; }

span.spoiler:hover, h2 {
 background: #D9AF9E !important; }

.file_reply, .postreply, .newsbody, .postreply_highlighted, td.label, span.hidden_thread {
 background: #E6CBC0 !important; }

.postreply span.spoiler:hover {
 background: #e3b4a0 !important; }

.board_newposts {
 font-weight:bold;
 background: #EDDAD2 !important; }

.file_thread, .file_reply {
 margin: 5px 10px 20px 5px !important; padding: 5px !important; }

.file_thread {
 border: 1px dashed #D9AF9E !important; }

.file_reply {
 border: 1px dashed #D9AF9E !important; }

.postreply, .newsbody, table, tr, td, td.label, span.hidden_thread {
 border: none !important; }

.postreply_highlighted {
 background: #EDDAD2 !important;
 border: 1px dashed #FF8040 !important; }

span.quote {
 color: #789922 !important; }

span.spoiler {
 color: #e3b4a0 !important; background: #e3b4a0 !important; }

span.spoiler:hover {
 color: #800517 !important; }

span.nav_group_button {
 background: #D9AF9E !important;
 border-color: #D9AF9E !important;
 color: #000000 !important; }

.postheader .postdate, .site_slogan {
 color: #800517 !important; }

.postreply .postdate, .postreply_highlighted .postdate, .postheader a.quotelink {
 color: #800517 !important; }

.postername {
 color: #117743 !important; }

a, a.quotelink:hover, .postreply_highlighted a.quotelink, .boardlist a {
 color: #800517 !important; }

.postreply a.quotelink {
 color: #800517 !important; }

.postform ul {
 font-size: 70% !important; }

a {
 text-decoration: none !important; color: #0F0C5D !important; }

.boardlist a {
 color: #000000 !important;
 font-size: 90% !important;}

.boardlist a:hover {
 background: #e3b4a0 !important; color: #C11B17 !important; }


/* MOD-SCHUCHTELEI */
div[style*="border: 5px solid rgb(255, 0, 0); padding: 20px; background-color: rgb(255, 255, 255); font-weight: bold; font-size: 15px;"] { color: red; }
div[style*="border: 5px solid rgb(255, 0, 0); padding: 20px; background-color: rgb(255, 255, 255); font-weight: bold; font-size: 15px;"] a {color: red !important; text-decoration: underline !important; }



img[src*="/images/button-expand.gif"] {
 padding: 0px 0px 15px 15px !important; width:0px !important; height:0px !important;
background: url("data:image/gif;base64,R0lGODlhDwAPAKEDAHRCLsyUfuXIvf///yH5BAEAAAMALAAAAAAPAA8AAAIwXI6JBu3vDmyiViAnvXlagQWaBYCdQ5YhlEYiWF6QBLYPbd+vrOF5c9IADYpiYFAAADs=") no-repeat !important; }

