css:
@namespace url(http://www.w3.org/1999/xhtml);@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  
 {  
#dashboard_index, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,  #dashboard_members, #dashboard_edit_post, #dashboard_followers, #dashboard_folowing{

background-image:url('http://i52.tinypic.com/ng9c47.png') !important;

background-repeat:no-repeat !important;

background-attachment:fixed;

background-position: left top !important;

background-color:#000 !important;

font-size:12px;

margin-left:30px;

}



ol#posts li blockquote,body.mceContentBody blockquote 
{margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 2px !important;
border-color: #4d3d46 !important;}


ol#posts li.post.new_post {

background: #ffffff url('http://i53.tinypic.com/34q9oy0.png') center center no-repeat;

}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
width:0;
height:70px;
padding-right:250px;
opacity: 0;

}

.new_post_label{

font-size:10px !important;

padding-top:5px !important;

font-family:tahoma !important;

font-style:bold !important;

text-transform: uppercase !important;

color:#4f4751 !important;

letter-spacing:1px !important

}




ol#posts li blockquote blockquote,body.mceContentBody blockquote blockquote {
border-color: #a383af !important;}

ol#posts li blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote {border-color: #7f7085 !important;}

ol#posts li blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #604f67 !important;}

ol#posts li blockquote blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote blockquote {
border-color: #392840 !important;}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {    
background: rgba(0,0,0,0) !important;  


 }


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {   
background: rgba(0,0,0,0) !important;
-webkit-border-radius: 20px;    
-moz-border-radius: 20px;    
border-radius: 20px;     
padding: 20px 20px;  }

h1 {color: #4d0854;
text-shadow: 0 0 3px #ffffff;}

.no_posts_found, h1.dashboard_header {
color: #764a71;}

img#content_top, img#content_bottom {
display: none;}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {    
background: none; 
color }

#right_column .dashboard_nav_item {    
background: none;    
background-attachment: initial;    
background-position-x: 50%;    
background-position-y: 100%;    
background-origin: initial;    
background-clip: initial;    
background-color: initial;}

#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: transparent;}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: rgba(0,0,0,0.5);}

#nav .nav_item.active {    
background: rgba(48,0,54,0.5) !important;  }

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {    
background: rgba(48,0,54,0.5) !important;  }

ol#posts li.notification.alt {    
background-color: #000;
border-bottom: 1px solid #310836;
color: #754c72;}

ol#posts li.notification.first_notification {    
background-color: #000;
color:#754c72;
border: 1px solid #310836;}

ol#posts li.notification {    
background-color: #000;
color:#754c72;
border-bottom: 1px solid #310836;}

ol#posts li.notification.last_notification {    
background-color: #000;
color:#754c72;
border-bottom: 1px solid #310836;}

ol#posts li.post .permalink {    
background-color: transparent !important;}
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.8);border: solid #310836;}

ol#posts li.post .post_avatar{border-bottom: 1px solid #310836;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {border-top: 1px solid #310836;border-bottom: 1px solid #310836;}

#right_column a#dashboard_switch_blog_menu_current:hover {
background: none;}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {background: none;}

form.dashboard_options_form {
background-color: rgba(0,0,0,0.7);
border-bottom: 1px solid #310836;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;display: block;
font-size: 11px;
margin-bottom: 25px;
padding: 15px 22px;}

form.dashboard_options_form .option_container {
border-top: 1px solid #310836;}

.no_posts_found, h1.dashboard_header {
color: #4d0854;}

.source_url, .tags {display: inline !important; white-space: normal !important;}
.source_url_gradient {display: none !important;}

#auto_pagination_loader {



background: none;



border-top: 0px solid #1B2D42;



}



}
