css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); 

#appmenu-button-container {
	background-clip: padding-box !important;
	border-radius: 3px 3px 0 0 !important;
        border: 1px solid rgba(90,90,90,0.7) !important;
	/*border-top: none !important;*/
        border-bottom: none !important;
        /*margin-top: -6px;*/
        margin-bottom: 0;
        padding: 0;
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(rgba(200,200,200,.55), rgba(150,150,150,.55), rgba(70,70,70,.7)) !important;
	background-repeat: no-repeat !important;
        height: 21px !important;
}

#appmenu-button {
        background: none !important;
	min-width: 60px !important;
	width: 66px !important;
	max-height: 27px !important;
	border: none !important;
	box-shadow: none !important;
}

#appmenu-button-container:hover {
        border: 1px solid rgba(100,100,100,1) !important;
	/*border-top: none !important;*/
        border-bottom: none !important;
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(-90deg, rgba(255,255,255,.7), rgba(255,255,255,.3), rgba(255,255,255,.2)) !important;
	background-repeat: no-repeat !important;
	background-clip: padding-box !important;
}

#appmenu-button .button-text {
	display: none !important;
}

#appmenu-button dropmarker {
	margin: 2px 0 0 -1px !important;
}

#main-window[privatebrowsingmode] #appmenu-button-container {
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(-90deg, rgba(153,38,211,.8), rgba(153,38,211,.3)) !important;
	background-repeat: no-repeat !important;
	border-color: rgba(5,8,167,.8) !important;
	background-clip: padding-box !important;
}

#main-window[privatebrowsingmode] #appmenu-button-container:hover {
	/*border-radius: 2px 2px 0 0 !important;*/
        border: 1px solid rgba(100,100,100,1) !important;
	/*border-top: none !important;*/
        /*margin-top: -6px;*/
        margin-bottom: 0;
        padding: 0;
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(-90deg, rgba(255,255,255,.7), rgba(255,255,255,.3), rgba(255,255,255,.2)) !important;
	background-repeat: no-repeat !important;
	background-clip: padding-box !important;
}
