css:
@-moz-document domain('pastebin.fossnet.info'), url-prefix('http://pastebin.fossnet.info')
{
.container{width:97% !important;padding:0px !important;
margin-left:auto !important;margin-right:auto !important;
border-width:0px !important;} 

#container{width:100% !important;padding:0px !important;
margin-left:auto !important;margin-right:auto !important;
border-width:0px !important;} 

.content{width:100% !important;padding:0px !important;
margin-left:auto !important;margin-right:auto !important;
border-width:0px !important;} 

/* the paste page "paste basing on this one" interface */
.replies{
width:97% !important;padding:0px !important;
margin-left:auto !important;margin-right:auto !important;}

/* the pasted box */
.paste {
width:97% !important;
margin-left:auto !important;
margin-right:auto !important;
border-width:0px !important;
}
.text_formatted {
width:100% !important;
padding-bottom:20px !important;padding-top:20px !important;
margin-left:auto !important;margin-right:auto !important;
border-width:0px !important;}


.text_formatted, .replies, .content
{
border:1px dotted darkblue !important;
}

body{
background-color:#88aa88 !important;
background-image:none !important;
padding:0px !important; 
margin: 0px !important;
}

.title,.active
{
color:#cceecc !important;
}

.footer,a
{
color:#003366 !important;
font-weight:bold !important;
}

.form_wrapper{
width:100% !important;
}
textarea{
font-family:monospace !important;
width:97% !important;
}
}
