css:
/* NieNova - 2011 - JVC - CDV - Zombie Emeraude for Nonchesque
*/

@-moz-document url-prefix("http://www.jeuxvideo.com/profil/") { 

#cdv_page {
border-color: rgb(200,200,200) !important;
}

#cdv_header {
background: url("http://s2.noelshack.com/uploads/images/21256428394575_header_emeraude_2_pour_lautre.png") no-repeat scroll left top #DDDDDD !important;
}

#rang span {
background: url("http://s2.noelshack.com/uploads/images/10760028739515_zombie_logo_test.png") no-repeat scroll left top transparent !important;
}

#pseudo:after {
content: "- By NieNova -";
text-transform: capitalize;
color: rgb(0,150,0);
font-size: 80%;
left: 520px !important;
top: 15px !important;
position: absolute !important;
}

#onglets li, #onglets li a, #onglets li a:hover {
color: rgb(50,290,50) !important;
}

#onglets li:not([class="on"]), #onglets li a {
background: url("http://s2.noelshack.com/uploads/images/476975827647_onglet_emeraude.png") no-repeat top left !important;
}

#onglets li a:hover {
background: green !important;
-moz-border-radius: 2px 2px 0px 0px !important;
}

h2, #td_niveau strong, #td_pts strong {
color: rgb(30,100,30) !important;
}

.msg1 {
background-color: rgb(240,300,240) !important;
}

#pt_jeuonline li {
background-color: rgb(100,220,100) !important;
}

#nbpost img, #nbniv img {
background:url("http://s2.noelshack.com/uploads/images/2133809787681_nb_de_psote_emeraude.png") repeat-x scroll left bottom transparent !important;
}

}
