css:
@-moz-document domain(gamefaqs.com) {

a {color: #0033CC !important;}
a:visited {color: #7698FF  !important;}
body {background: #171717 !important;color: #E6E6E6 !important;}

body > #header a {color: #00FF00 !important;}
body > #header {background: #0000B8 !important;border-bottom: 2px solid #000080 !important;}
body > #body {background: #050505 !important;color: #FFF !important;}
body > #footer {background: #0000B8 !important;border-top: 2px solid #000080 !important;} 
body > #footer a {color: #00FF00 !important;}
div#pagetop {border-bottom: 2px solid #000080 !important;}
div#pagetop a {color: #00FF00 !important;}
div#pagebottom {border-top: 2px solid #000080 !important;}
div#pagebottom a {color: #00FF00 !important;}

/* MASTHEAD
*******************************/
#header, 
#searchbox, 
#quicknav, 
#quicknav a, 
#quicknav p, 
#platformlist, 
#masthead .mh_wrap {background-image:url('http://img710.imageshack.us/img710/2272/bgmastheadv11blue.png')!important;}

/* HEADER */
#logo { background-image: url('http://img202.imageshack.us/img202/7449/logobluet.png') !important;}

/* LOGIN */
#loginbox {background: url('http://img705.imageshack.us/img705/5131/loginboxblue.png') no-repeat scroll 0 8px !important;}
#loginbox .msg {color: #FFF !important;}
#loginbox .nav a {border-right: 1px solid #D9F3F8 !important;color: #D9F3F8 !important;}
#loginbox .nav a.last {border: medium none !important;}
#loginbox span.data {background: #0E0E0E !important;border: 1px solid #666 !important;}
#loginbox span.submit {border: 1px solid #000080 !important;display: block !important;height: 22px !important;width: 48px !important;}
#loginbox span.submit input {color: #000 !important;}
div#loglink > a{ color: #FFF !important;}

/* SEARCH */
#searchbox .name a {color: #FFF !important;}
#searchbox select {background-color: #000!important;border: 1px solid #666 !important;}
#searchbox .data {background-color: #000 !important;border: 1px solid #666 !important;}
span.data > input#searchtextbox.text {background-color: #000 !important;}

/* QUICK NAV -- Primary Nav */
#quicknav a:visited {color: #6495ED !important;}
#quicknav .mh_wrap p {background: #141414 url('http://img847.imageshack.us/img847/5669/quicknavdivideralt.png') no-repeat center left !important;}
#quicknav a {background: #141414 url('http://img847.imageshack.us/img847/5669/quicknavdivideralt.png') no-repeat center right !important;color: #6495ED !important;height: 32px !important;line-height: 32px !important;}

/* PLATFORM LIST */
#platformlist {color: #999 !important;border-bottom: 1px solid #777 !important;}
#platformlist .nav b {color: #FFF !important;}
#platformlist form.jumper select {background-color: #000 !important;border: 1px solid #666 !important;}
div#sys.jumper > form.jumper > select#jumpmenu {background-color: #1E1E1E !important;}
#platformlist a:visited {color: #0033CC !important;}

/* SITE FOOTER
*******************************/
#footer {background: url('http://img189.imageshack.us/img189/5924/footerbgv.png') no-repeat scroll 0 0 #050505 !important;}
#footer #sitenav {background-color: #1E1E1E !important;}
#cnetfooter, #cnetfooter a, #todayoncnet, #todayoncnet a {color: #FFF !important;}


/* LAYOUT
*******************************/
#content {background: #000 !important;border-color: #888 !important;}
#container {background: #171717 url('http://img508.imageshack.us/img508/9694/containerlight.png') repeat-x top !important;}

/* COLUMNS */
#content #side_col {background: url('http://img269.imageshack.us/img269/3522/contentsidecol.png') no-repeat scroll 0 0 transparent !important;}

/* COLUMN LAYOUTS */
#mpu_col_wrap, #sky_col_wrap {background: url('http://img848.imageshack.us/img848/1814/layoutbg.png') repeat-y -222px 0 !important;}
#gamespace_noback #mpu_col_wrap {background: none repeat scroll 0 0 transparent !important;}
#mpu_col_wrap{background-position: right 0 !important;}
#mpu_col_wrap .col_layout{padding: 0 324px 0 10px !important;}
#mpu_col_wrap #side_col{margin: 0 -324px 0 10px !important;}
#sky_col_wrap{background-image: none !important;}
#sky_col_wrap .col_layout{padding: 0 180px 0 10px !important;}
#sky_col_wrap #side_col{background-image: none !important;margin: 0 -170px 0 10px !important;padding: 0 !important;}

/* FORM BUTTONS */
input.go, span.go, .btn, .btn .btn_wrap {background-image: url('http://img824.imageshack.us/img824/4846/btnv11.png') !important;}
.btn, .btn button, .btn input, .btn span {color: #FFF !important;}
/*vote button*/
[src="http://image.com.com/gamefaqs/images/poll_vote.gif"] {width: 0 !important;padding: 32px 43px 0 0 !important;background: url('http://img696.imageshack.us/img696/8704/pollvote.png') !important;}

/* HEADERS -- H1 - H6 */
#content .head {border-color: #666 !important;color: #FFF !important;}
#content .head h1 {color: #FFF !important;}
#content .head h1 a:hover {text-decoration: underline !important;}

/* table elements */
div.message {background-color: #050505 !important;}

/* TABLE -- CONTRIBUTIONS */
table.contrib tbody tr td span[style] {color: #00FF00 !important;}
span.updated {color: #00FF00 !important;}

/* TABLE -- BRACKETS */
table.bracket td.divh {background: #424242 !important;color: #FFF !important;}
table.bracket {border: 1px solid #666 !important;}
table.bracket td {border: 1px solid #666 !important;}

/* TABLE -- DOWNLOADS */
#side_col table.downloads td {background: url('http://img14.imageshack.us/img14/7595/dotl.png') repeat-x scroll left bottom !important;padding: 5px 0 !important;}

/* LISTS -- SYSTEMS */
#content ul.systems li.even {background: #050505 none repeat scroll 0 0 !important;}

/* LISTS -- CHEATS */
#content ul.cheats li.cheat {background: url('http://img14.imageshack.us/img14/7595/dotl.png') repeat-x scroll 0 bottom !important;}
#content ul.cheats li.last {background: none !important;padding: 0 !important}

/* CONTENT FILTERS */
.content_filter .wrap {background: url('http://img200.imageshack.us/img200/1963/contentfilterlight.png') no-repeat 0 0 !important;}
.content_filter .w1 {background-position: right 0 !important;}
.content_filter .w2 {background-position: 0 bottom !important;}
.content_filter .w3 {background-position: right bottom !important;}
.content_filter .genre {border-top: 1px solid #666 !important;}

/* CONTENT NAV */
ul.content_nav li {background: url('http://img820.imageshack.us/img820/7508/gamespacenavtabbgn.png') no-repeat scroll left top transparent !important;}
ul.content_nav li a {background: url('http://img820.imageshack.us/img820/7508/gamespacenavtabbgn.png') no-repeat scroll right top transparent !important;color: #B0E0E6 !important;display: block !important;float: left !important;}
ul.content_nav li a:hover, ul.content_nav li.hi a:hover {color: #0033CC !important;}
ul.content_nav a b.first {background: url('http://img141.imageshack.us/img141/9100/gamespacenavtablbgn.png') no-repeat top left !important;}
ul.content_nav a b.last {background: url('http://img831.imageshack.us/img831/6418/gamespacenavtabrbgn.png') no-repeat top right !important;}
ul.content_nav li.hi, ul.content_nav li.hi a {background-image: url('http://img837.imageshack.us/img837/443/gamespacenavtabhibgnhov.png') !important;}
ul.content_nav li.hi a {color: #0033CC !important;}
ul.content_nav li.hi a.last {background-image: url('http://img263.imageshack.us/img263/1631/gamespacenavtabhirbgn.png') !important;}
ul.content_nav li.hi a b.first {background-image: url('http://img200.imageshack.us/img200/2306/gamespacenavtabhilbgn.png') !important;}
ul.content_nav li.hi a b.last {background-image: url('http://img263.imageshack.us/img263/1631/gamespacenavtabhirbgn.png') !important;}

/* POD BODY -- side */
#side_col .pod {background: url('http://img850.imageshack.us/img850/5346/sidecolpod.png') no-repeat left bottom !important;}
#side_col .pod .body, #side_col .pod .foot {background: #000 !important;border-color: #666 !important;}

/* POD -- Lists */
#side_col .pod ul.items li {background: url('http://img14.imageshack.us/img14/7595/dotl.png') repeat-x left bottom !important;}
#content .contrib .body {background: #050505 !important;}
table.bracket .correct {color: #000 !important;}

/* The following "POD -- " styles must come after the above "POD" styles */
/* POD -- CONTRIB */
#content .contrib .body {background: #050505 !important;}

/* OTHER CONTENT
*******************************/
/* CROSS GAMESITES CONTENT -- .gs may be a part of some pods or a html element around a pod */
#content .gs a{color:#B0E0E6 !important;}
#content .gs li.feature {background: url('http://img14.imageshack.us/img14/7595/dotl.png') repeat-x left bottom !important;}

#main_col .gs .body .item {background: url('http://img14.imageshack.us/img14/7595/dotl.png') repeat-x left bottom !important;}
#main_col .gs .body .last {background: transparent none repeat scroll 0 0 !important;}

/* CROSS GAMESITES CONTENT -- FEATURES */
#main_col .features .pod {background-color: #050505 !important;margin: 0 !important;}
#content .pod div.head {background: none repeat scroll 0 0 #0000B8 !important;border: 1px solid #000079 !important;color: #FFF !important;line-height: 16px !important;padding: 6px 10px !important;}
#content .gs div.head {background: none repeat scroll 0 0 #464646 !important;border: 0 none !important;color: #202020 !important;}
#content .gs div.head h1, #content .gs div.head .title {color: #FFF !important;}

/* TODAY
*******************************/
#main_col .today .head .date {color: #B0E0E6 !important;}
#main_col .today .head {border-bottom: 1px solid #909090 !important;}
#main_col .today .head h1 {color: #FFF !important;}

/* TOP TEN
*******************************/
#main_col .topten .pod ol li.alt {background-color: #050505 !important;}
#main_col .topten .pod .head {border-color: #000079 !important;}
.head h2 a {color: #FFF !important;}

/* RELEASES
*******************************/
#content .releases td {background: #050505 none repeat scroll 0 0 !important;}
#content .releases tr.even td {background: transparent none repeat scroll 0 0 !important;}
.releases li.region .region {color: #FFF !important;}
#main_col .body .systems li.even {background-color: #050505 !important;}
#content .contrib .body {background: #050505 !important;}

/* QNA
*******************************/
/* QNA HUB LAYOUT */
#content .qna_hub {background: #000 !important;}
/* QNA HUB LAYOUT -- SIDE COLUMN */
#content .content_qna h2 {color: #FFF !important;}
#content .qna_hub #side_col li .console {color: #D9F3F8 !important;}
#content .qna_hub #side_col .header {background: #000 url('http://img43.imageshack.us/img43/3624/headqnagradient.gif') repeat-x left top !important;border: 1px solid #2A2A2A !important;}
#content .qna_hub #side_col .header h3 {color: #FFF !important;}
#content .qna_hub #side_col li.alt {background: #050505 none repeat scroll 0 0 !important;}

#content .content_qna .module {background: #050505 none repeat scroll 0 0 !important;border: 1px solid #404040 !important;}

#content .content_qna .header {background: url('http://img43.imageshack.us/img43/3624/headqnagradient.gif') repeat-x left top !important;border: 1px solid #2A2A2A !important;}
#content .content_qna .header_small {background: #0000B8 none repeat scroll 0 0 !important;color: #FFF !important;}
#content .content_qna .header_small a {color: #B0E0E6 !important;}

#content .content_qna .title_bar {background: #050505 !important;color: #FFF !important;}
#content .content_qna .title_bar span {color: #FFF !important;}
#content .content_qna .title_bar a {color: #B0E0E6 !important;}

/* hr is rendered inconsistently across browsers; style the wrapper instead */
#content .content_qna .divider {border-top: 1px solid #666 !important;}
#content .content_qna .view_block {background: #0000B8 none repeat scroll 0 0 !important;border: none !important;color: #FFF !important;}
#content .content_qna .view_block a {color: #D9F3F8 !important;}
#content .content_qna .view_block {background: #0000B8 none repeat scroll 0 0 !important;border: none !important;color: #FFF !important;}
#content .content_qna .view_block a {color: #D9F3F8 !important;}

/* forms */
#content .content_qna form input, #content .content_qna form select, #content .content_qna form textarea {background-color: #000 !important;}
#content .content_qna form input.submit {background: url('http://img97.imageshack.us/img97/7928/btnbgmed.png') !important;color: #FFF !important;padding-left: 0; padding-right: 0; padding-top: 0; padding-bottom: 2px}

#content .content_qna fieldset.searchbox span.data span input, #searchbox li.search_term .data input, .searchtopics .data span input, #loginbox span.data input {border-style: none !important;border-width: 0px !important;}
#content .content_qna fieldset.searchbox {background: #5D5D5D none repeat scroll 0 0 !important;border: 1px solid #787878 !important;}
#content .content_qna fieldset.searchbox label {color: #FFF !important;}
#content .content_qna fieldset.searchbox span.data span {background: #000 none repeat scroll 0 0 !important;}
#content .content_qna fieldset.searchbox input.submit_search {color: #0033CC !important;background-color: #050505 !important;}

#content .content_qna .ask_question fieldset select {background-color: #000 !important;border: 1px solid #2A2A2A !important;}
#content .content_qna .ask_question fieldset submit {background-color: #000 !important;border: 1px solid #2A2A2A !important;}

/* tables */
#content .content_qna div.qna_table {background: #000 none repeat scroll 0 0 !important;}
#content .content_qna .qna_table th {background: #0000B8 none repeat scroll 0 0 !important;color: #FFF !important;}
#content .content_qna .qna_table td {border-top: 1px solid #666 !important;}
#content .content_qna .qna_table .grey {background: #6E6E6E none repeat scroll 0 0 !important;color: #FFF !important;} 
#content .content_qna .qna_table tr.footer, #content .content_qna .qna_table tr.highlighted {background: #0F0F0F none repeat scroll 0 0 !important;}
#content .content_qna .qna_table .question span {color: #00FF00 !important;}

/* hub page */
#content .content_qna.hub .header h2 {color: #FFF !important;}

/* question page */
/* question */
#content .content_qna .question_pod .header_small {border: 1px solid #000 !important;}
#content .content_qna .question_pod .body {background-color: #000 !important;color: #FFF !important;}
#content .content_qna .question_pod {background: #000 !important;}
/* shared */
#content .content_qna .with_rating .body .rating {background: url('http://img19.imageshack.us/img19/1562/thumbsupdown2.png') no-repeat scroll left top !important;}
#content .content_qna .with_rating .body .rating.up {background-position: 0 -40px !important;}
#content .content_qna .with_rating .body .rating.down {background-position: 0 -20px !important;}
/* answers */
#content .content_qna .answer_pod {background: #000 !important;}
/* accepted answer */
#content .content_qna .accepted_answer .body {background-color: #000 !important;}
#content .content_qna .accepted_answer .title_bar {border: 1px solid #000 !important;}
#content .content_qna .accepted_answer .title_bar h3, #content .content_qna .accepted_answer .title_bar span {color: #FFF !important;}
#content #sky_col_wrap .content_qna .head h1, #content .content_qna h2 {color: #FFF !important;}
#content .content_qna .accepted_answer .title_bar {background: #6E6E6E none repeat scroll 0 0 !important;border: 1px solid #000 !important;}
#content .content_qna .accepted_answer {background: #000 !important;}

/* BOARDS
*******************************/

/* BOARD NAV */
.board_nav .body {background: #000 none repeat scroll 0 0 !important;}
.board_nav div.user {color: #FFF !important;}
.board_nav div.pages {color: #FFF !important;}

/* SEARCH TOPICS */
.searchtopics {background-color: #050505 !important;}
.searchtopics .data span {background-color: #1E1E1E !important;border: 1px solid #444 !important;}

/* PAGE JUMPER */
#content .pagejumper li {border-left: 1px solid #888 !important;}
#content .pagejumper li.first {border: medium none !important;padding-left: 0 !important;}

/* BOARD TABLE */
table.board tr:not([class*="friend"]) td:not([class*="friend"]), table.board th, tbody.even td, tbody.even th, tr.even:not([class*="friend"]) td:not([class*="friend"]), tr.even th {background-color: #050505 !important;}
table.board tr.even:not([class*="friend"]) td:not([class*="friend"]), table.board tr.even th {background-color: #000 !important;}
#content thead th, #content tr.head th {background: #0000B8 !important;color: #FFF !important;font-size: 11px !important;padding: 2px 5px !important;text-align: left !important;}
table.topics {	color: #B0E0E6 !important;}
div.content_nav h1 {color: #FFF !important;}

/* LEAVE THE FOLLOWING BELOW TABLE.BOARDS TO INHERIT BOARD STYLES */

/* BOARD TABLE -- MESSAGES */
/* Usage: <table class="board message"> */
table.message td {border-color: #777 !important;}

/* USER INFO TABLE */
table.userinfo {border-top: 2px solid #555 !important;}

/* TEXT AREA */
select, textarea, input[type=text], input[type=password] {-moz-appearance: none !important;}
input[type=text], input[type=password] {color: #FFF !important;background-color: #1E1E1E !important;border: 1px inset #444 !important;}
textarea {color: #FFF !important;background-color: #1E1E1E !important;border: 1px solid #909090 !important;} 
select{ color: #FFF !important;background-color: #1E1E1E !important;border: 1px inset #444 !important;}

/* ANNOTATED TOU */
div#main_col.col div.body ul span[style], div#main_col.col div.body p span[style] {color: #0033CC !important;}

/* ADS
*******************************/
.xpromolink, .rubics_netxp1_main {color: #FFF !important;}
.xpromositename {color: #00FF00 !important;}

/* FAQS PAGE & ICONS
******************************/
[src="http://image.com.com/gamefaqs/images/default/rec.gif"], [src="/images/default/rec.gif"] {width: 0 !important;background-image: url('http://img4.imageshack.us/img4/1413/recxg.png') !important;padding-left:0; padding-right:12px; padding-top:12px; padding-bottom:0}  		
[src="http://image.com.com/gamefaqs/images/default/s3.gif"] {width: 0 !important;background-image: url('http://img171.imageshack.us/img171/765/98276615.png') !important;padding-left:0;padding-right:12px; padding-top:12px; padding-bottom:0}	
[src="http://image.com.com/gamefaqs/images/default/s2.gif"] {width: 0 !important;background-image: url('http://img810.imageshack.us/img810/540/88065366.png') !important;padding-left:0; padding-right:12px; padding-top:12px; padding-bottom:0}
.ffaq div.ftoc {background-color: #050505 !important;border-color: #666 !important;}
.ffaq h3 {color: #FFF !important;}
.ffaq div.spoiler_box {background-color: #050505 !important;border-color: #666 !important;}
.ffaq div.section_box {background-color: #1E1E1E !important;border-color: #666 !important;}
table.ffaq {border-color: #888 !important;}
table.ffaq >tbody >tr >td.ffaq {border-color: #888 !important;}
.ffaq h2 {border-color: #666 !important;}


/* PRIVATE MESSAGE ICON */
[src="http://image.com.com/gamefaqs/images/default/s1.gif"] {width: 0 !important;background-image: url('http://img834.imageshack.us/img834/406/88622197.png') !important;padding-left:0; padding-right:12px; padding-top:12px; padding-bottom:0}

/* BOARD USER INFO LINKS */
a[href^="/boards/bman.php"][href*="move=alpha"] {color: #00FF00 !important;}


/* GUTTERS
******************************/
#gutters {background: url('http://img710.imageshack.us/img710/6315/guttermidlight.png') repeat-y scroll center 0 transparent !important;}
.gutter {float: left !important;width: 100% !important;}
#gutter_top {background: url('http://img190.imageshack.us/img190/8506/guttertoplight.png') no-repeat scroll center 0 !important;}
.has_lead .ad_giant, .has_lead .ad_super {background: url('http://img541.imageshack.us/img541/7109/guttergiant.png') no-repeat scroll center bottom transparent !important;}


/*GAMESPACE  
******************************/
#content .body .rating {background: url('http://img19.imageshack.us/img19/1562/thumbsupdown2.png') no-repeat scroll left top transparent !important;}
#side_col .pod .fboards ul, #side_col .pod .similar ul {background-color: #050505 !important;}
#side_col .pod .fboards li, #side_col .pod .similar li {border-color: #000 !important;}
div#related_games > div.pod, div#gamespace_info > div.pod {background-image: none !important;}
#side_col .pod .infobox .gameinfo h1 {color: #FFF !important;}
#glyde_shopper_module .ghead {background: #0000B8 !important;border-color: #000079 !important;}
#glyde_shopper_module .ghead .more span {background-image: url('http://img850.imageshack.us/img850/9523/glydeiconblue.png') !important;}
#glyde_shopper_module {background-image: url('http://img13.imageshack.us/img13/2001/glydesprite.png') !important;}
#glyde_shopper_module .glyde_sell_button, 
#glyde_shopper_module .sell, 
#glyde_shopper_module .glyde_price .sign, 
#glyde_shopper_module .glyde_price .dollars, 
#glyde_shopper_module .glyde_price .cents {color: #FFF !important;}
div.footer_link_wrap {background-image: url('http://img146.imageshack.us/img146/2614/glydevariantfootbglt.png') !important;}

/*USER PANEL (used for Topic list display in right column and header)
******************************/
#content .pod .user_panel .body {background: url('http://img219.imageshack.us/img219/6003/userpaneltop.png') repeat scroll 0 45px transparent !important;}
#content .pod .user_panel .body span {background: url('http://img219.imageshack.us/img219/6003/userpaneltop.png') no-repeat scroll right top transparent !important;}
#content .pod .user_panel .bottom {background: url('http://img64.imageshack.us/img64/5900/userpanelbottom.png')repeat scroll 0 7px transparent !important;}
#content .pod .user_panel .bottom span {background: url('http://img64.imageshack.us/img64/5900/userpanelbottom.png') repeat scroll right top transparent !important;}
#content .pod .user_panel .u_search {background: none repeat scroll 0 0 #1E1E1E !important;border-left: 1px solid #666 !important;border-right: 1px solid #666 !important;}
#content .pod .user_panel .u_pagenav {background: none repeat scroll 0 0 #1E1E1E !important;border-left: 1px solid #666 !important;border-right: 1px solid #666 !important;}
#content .pod .user_panel .u_search .data span {background: none repeat scroll 0 0 #000 !important;border-color: #666 -moz-use-text-color #666 #666 !important;}
#content .pod .user_panel .u_search .data input {background: none repeat scroll 0 0 transparent !important;border: none !important;}
input.go, span.go, .searchbtn, .searchbtn .searchbtn_wrap {background-image: url('http://img713.imageshack.us/img713/5269/searchbut.png') !important;height: 26px !important;}
.searchbtn, .searchbtn button, .searchbtn input, .searchbtn span {color: #FFF !important;}
#side_col .pod .infobox .gameinfo h1, h2 {color: #FFF !important;}
}

/*FAQs PAGE
******************************/
@-moz-document url-prefix("http://db.gamefaqs.com") {

body {background: #050505 !important;color: #FFF !important;}
}
