css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix(https://www.google.com/accounts/) {



/*---- BACKGROUND ----*/

body[onload*="a"], body[vlink|="blue"] {

/* PUT URL OF IMAGE */

background:  #262626 !important;}



/* Font */

span, input, label {

font-family: "Lucida Grande", sans-serif !important;}



/* Account *

body[dir="ltr"] div.body {background: rgba(255, 255, 255, 0.9) !important;}

*/





/*---- HIDE ----*/

/* Accounts */

#main .footer,

/* Calendar */

#main > div:first-child + table > tbody > tr:first-child > td:first-child > table,/*Organize your schedule...*/

#main > div:first-child + table > tbody > tr:first-child > td:first-child + td > table[bgcolor="#e8eefa"], /*Create account */

#main > table:first-child + table > tbody > tr > td:first-child + td > table:last-child, /*Day in the Cloud*/

#main > table:first-child + table > tbody > tr:first-child > td:first-child + td > table[bgcolor="#e8eefa"] + br + font,

#main .hrule, /*Footer*/

/* Reader */

#maincontent > table:first-child > tbody > tr > td:first-child, /*Have trouble keeping...*/

#main > table:first-child > tbody > tr:first-child + tr > td:last-child, /*Read your favorite...*/

#main > table:first-child + br + table + br + br + div, /*Footer*/

#rhs_login_signup_box > table:last-child > tbody > tr > td, /*Create account*/

/* Mail */

body > table:first-child > tbody > tr > td:last-child,

body > table:first-child + table > tbody > tr:first-child,

body > table:first-child + table > tbody > tr:first-child + tr > td:first-child,

#ga-fprow,/* I cannot access my account */

#links,

body > table:first-child + table + br + table,

/* Sites */

#main #gaia_universallogin

{display: none !important;}



/*---- LOGO ----*/

#main > table:first-child > tbody > tr:first-child + tr > td:first-child, /*Calendar*/

#main div.header, /*Reader*/

body > table:first-child > tbody > tr > td:first-child /*Mail*/ {

z-index: 1 !important;

position: fixed !important;

top: 50% !important;

left: 50% !important;

margin-top: -130px !important;

margin-bottom: 250px !important;

margin-left: -81px !important;} /* -71 */



/* ADJUSTING POSITION  */

#main > table:first-child > tbody > tr:first-child + tr > td:first-child,

body > table:first-child > tbody > tr > td:first-child {margin-left: -61px !important;}



#main div.header,

#main > table:first-child > tbody > tr:first-child + tr > td:first-child img /*,

body > table:first-child > tbody > tr > td:first-child img*/{

-webkit-box-shadow: 0 0 20px 20px rgba(255, 255, 255, 0.9) !important;

-moz-box-shadow: 0 0 20px 20px rgba(255, 255, 255, 0.9) !important;

}



/* Mail */

body > table:first-child > tbody > tr > td:first-child img {visibility: hidden !important;}

body > table:first-child > tbody > tr > td:first-child {

background: /*url("https://mail.google.com/mail/images/2/5/logo.png")*/ url("https://mail.google.com/mail/images/2/5/logo1.png") no-repeat !important;}





/*---- LOGIN WINDOW ----*/

#gaia_loginbox {

position: fixed !important;

width: 470px !important;

height: 350px !important;

top: 50% !important;

left: 50% !important;

margin-top: -185px !important;

margin-left: -235px !important;



background: rgba(232, 232, 232, 0.9) !important;

-webkit-box-shadow: 0 5px 8px rgba(195, 195, 195, 0.6) !important;

-moz-box-shadow: 0 5px 8px rgba(195, 195, 195, 0.6) !important;}



#gaia_loginbox td[bgcolor*="#e8eefa"] {

background-color: transparent !important;}

.form-noindent {

background-color: transparent !important;

border: none !important;}



/* USER/PASSWORD */

#gaia_table {

z-index: 1 !important;

position: fixed !important;

top: 50% !important;

left: 50% !important;

margin-top: 5px !important;

margin-left: -130px !important;}



/*#gaia_table > tbody > tr:first-child + script + tr*/

#gaia_table > tbody > tr:first-child {

z-index: -1 !important;

position: fixed !important;

background: rgba(200, 200, 200, 0.6) !important;

top: 50% !important;

left: 50% !important;

width: 410px !important;

height: 100px !important;

margin-top: -10px !important;

margin-left: -205px !important;

color: transparent !important;



-moz-box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.7) !important;

-webkit-border-radius: 5px !important;

-moz-border-radius: 5px !important;}

#gaia_table > tbody > tr:first-child > td > table {display: none !important;}



/*---- FIX ----*/

input#Email, input#Passwd {width: 171px !important;}





/* SIGN IN */

input[class="gaia le button"] {

position: fixed !important;

-moz-appearance: none !important;

background: transparent url("data:image/png;base64,") 15px 0 no-repeat !important;

border: none !important;

width: 60px !important;

top: 50% !important;

left: 50% !important;

margin-top: 105px !important;

margin-left: -30px !important;

padding-top: 32px !important;

cursor: pointer !important;}



input[class="gaia le button"]:hover:active {

background: transparent url("data:image/png;base64,") 15px 0 no-repeat !important;}



}
