css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("homeandlearn.co.uk"),
domain("accountonline.com"),
domain("amazon.com"),
domain("regular-expressions.info"),
domain("urbandictionary.com"),
domain("mahalo.com"),
domain("imageshack.us") {

body, span, background, select, form, td, th, tr, p, dd, dt {
background:#111515 !important;
color:#CCCCCC !important;
}

/*
div {
background:none !important;
color:#CCCCCC !important;
}
*/

div {
background:#111515 ;
color:#CCCCCC;
}


ol {
  background-color:#253535 !important;
}

h1 {
  background:#253535 !important;
  color:#dddddd !important;
}

h2  {
  background:#000000 !important;
  color:#dddddd !important;
}

h3, h4, h5, h6 {
  background:inherit !important;
  color:#dddddd !important;
}

font, p { 
  color:#bbbbbb !important;
}

table {
  background:black !important;
}

ul {
  background:#223333 !important;
  color:#CCCCCC !important;
}

li, code {
  background:inherit !important;
  color:#dddddd !important;
}

strong, blockquote {
  background:#ffffff !important;
  color:#CCCCCC !important;
}

a:link {color:#FFCC66 !important; background:#111515 ; font-weight:bold !important; text-decoration:underline !important; }


a {color:#FFCC66 !important; background:#111515;}

/*
a:link {color:#FFCC66 !important; background:#111515 !important; font-weight:bold !important; text-decoration:underline !important; }
*/
a:visited {color:#FFB720 !important; background:#111515 !important;}
a:active {color:#ff2424 !important; background:#111515 !important;}
a:hover {color:red !important; background:#111515;}

input, input.post {
  color:#FFCC66 !important; background:#111515 !important;
}

/*
img {
  background:INHERIT !important;
}

/* -------------------------------------------------------- */

/*
Dark Style Textboxes
By XerBlade 
http://userstyles.org/styles/591
*/

pre, textarea {
   color: white !important;
   background: black !important;
   border-left-color: gray !important;
   border-right-color: gray !important;
   border-top-color: gray !important;
   border-bottom-color: gray !important;
}

/* Uncomment this part if you also want to apply to forms and the like. */
input:not([type="button"]):not([type="checkbox"])
:not([type="submit"]):not([type="reset"]), 
select {
    color: white !important;
    background: black !important;
    border-left-color: gray !important;
    border-right-color: gray !important;
    border-top-color: gray !important;
    border-bottom-color: gray !important;
} 

}
